INSERT INTO `recipes` (`id`, `name`, `describtion`, `image_name`) VALUES
(1, 'Blyneliai', '&lt;p&gt;Užvirintu pienu užpyliau manus ir palaikiau 5 minutes.&lt;/p&gt;&lt;p&gt;Kiaušinius išplakiau su žiupsneliu druskos ir cukrumi.&lt;/p&gt;&lt;p&gt;Į kiaušinių plakinį įmaišiau manus su pienu, varškę ir miltus.&lt;/p&gt;&lt;p&gt;Ištirpinau keptuvėje sviestą ir kepiau blynus.&lt;/p&gt;&lt;p&gt;Kiekvieną blyno pusę apkepiau po 1,5-2 minutes ant mažo kaitrumo ugnies.&lt;/p&gt;', '3241.jpg'),
(2, 'Amerikietiški vafliai', '&lt;p&gt;Įkaitinkite vaflių keptuvę. Ištepkite vieną vaflių pusė sviestu. Dėkite sūrį, obuolio griežinėlį ir ant viršaus uždėkite kitą vaflį. Dėkite į vaflinę ir kiek pakepkite.&lt;/p&gt;&lt;p&gt;P.s. vietoj šaldytų vaflių galite iškepti vaflių keptuvėje nelabai saldžius blynus.&lt;/p&gt;', '4060.jpg'),
(3, 'Morkos su pievagrybiais', '&lt;p&gt;Grybų dedu daugiau, nes taip skaniau man.:)&lt;/p&gt;&lt;p&gt;Morkas supjaustyti kubeliais, supilti truputį vandens, įberti druskos, pipirų, įdėti šaukštą sviesto. Uždengus troškinti ant silpnos ugnies.&lt;/p&gt;&lt;p&gt;Pievagrybius supjaustyti ir pakepinti svieste. Sudėti prie morkų, įberti dalį petražolių lapelių.&lt;/p&gt;&lt;p&gt;Viską sukrauti į riebalais pateptą indą, užbarstyti tarkuotu sūriu, užpilti grietine ir kepti karštoje orkaitėje, kol pasilydys sūris.&lt;/p&gt;&lt;p&gt;Paruošti petražolių lapeliais.&lt;/p&gt;&lt;p&gt;Šį patiekalą valgome kartu su virtomis bulvėmis.&lt;/p&gt;&lt;p&gt;SKANAUS:)&lt;/p&gt;', '3400.jpg'),
(4, 'Vištienos ir triušienos salotos', '&lt;p&gt;Mėsą susmulkinti, svogūnus supjaustyti, supilti žirnelius. Įtrupinti krapų, pagal skonį - druskos ir pipirų, išmaišyti su majonezu.&lt;/p&gt;', '5304.jpg'),
(5, 'Pyragas su špinatų-varškės įdaru', '&lt;p&gt;Visus tešlai skirtus produktus sumaišykite su maišytuvu arba suminkykite rankomis.&lt;/p&gt;&lt;p&gt;Tešla išklojame kepimo formą.&lt;/p&gt;&lt;p&gt;Špinatus susmulkinti ir sumaišyti su varške, kiaušiniais, grietine, smulkintu svogūnu ir prieskoniais.&lt;/p&gt;&lt;p&gt;Įdarą sudėti ant tešlos ir gausiai apibarstyti sūriu.&lt;/p&gt;&lt;p&gt;Kepti 180C laipsnių orkaitėje 45-50 minučių.&lt;/p&gt;', '3477.jpg'),
(6, 'Vaisių kokteilis', '&lt;p&gt;Obuolį, persiką ir ananasą nulupti, išvalyti ir smulkiai sutarkuoti (kaip tarkuojame bulves plokštainiui).&lt;/p&gt;&lt;p&gt;Vaisių tyrelę išplakti su ledais.&lt;/p&gt;&lt;p&gt;Skanaus! ! !&lt;/p&gt;', '2326.jpg'),
(7, 'Virtinukai', '&lt;p&gt;Į varškę įmušti kiaušinius, sudėti cukrų, supilti ištirpintą sviestą,  berti truputį druskos ir pamaišyti. Tada berti pusė miltų ir maišyti tešlą. Likusius miltus supilkite ant lygaus paviršiaus, o ant jų išverskite varškės masę ir minkykite, kol tešla taps vientisa. Formuokite pailgą volą, supjaustykite griežinėliais ir virkite verdačiame vandenyje, kol varškėčiai iškils į paviršių ir taps minkšti.&lt;/p&gt;&lt;p&gt;P.s. Virtinių galite iškarto  nevirti. Sudėkite į šaldymo indus ir užšaldykite.&lt;/p&gt;', '6482.jpg'),
(8, 'Seniai besmegeniai', '&lt;p&gt;Virtus ryžius, smulkintas krabų lazdeles, smulkintą svogūną, kiaušinius ir majonezo masę pertrinkite smulkintuvu.\n                                                                Švariomis drėgnomis rankomis formuokite kukulius. Juos dėkite ant krekerių ir darykite senius besmegenius. Puoškite kukurūzais, pipirais ar kuo tik sugalvosite.&lt;/p&gt;', '5602.jpg'),
(9, 'Keptos kepenėlės su obuoliais', '&lt;p&gt;Kepenėles supjaustyti norimo dydžio gabaliukais, apkepti keptuvėje su karštu aliejumi, pasūdyti ir papipirinti.\n                                                                Nuo obuolių nulupti žievę , išimti sėklalizdžius, supjaustyti griežinėliais ir sudėti kepti su kepenėlėmis.\n                                                                Tada susmulkinti svogūną ir taip pat sudėti kepti. Baigiant kepti dėti kelis šaukštus grietinės arba majonezo ir dar šiek tiek patroškinti.&lt;/p&gt;&lt;p&gt;Šaltinis: Savaitraštis &quot;TV savaitė&quot;&lt;/p&gt;', '6586.jpg'),
(10, 'Citrininis pyragas', '&lt;p&gt;Įkaitinti orkaitę iki 200°C.\n                                                                Mažame puode ištirpinti sviestą.\n                                                                Kiaušinius išplakti su cukrumi, vaniliniu cukrumi ir vanilės sėklomis.\n                                                                Supilti ištirpintą sviestą ir citrinos sultis, suberti tarkuotą žievelę.\n                                                                Sluoksniuotą tešlos lakštą įkloti į kepimo indą ir subadyti šakute.\n                                                                Supilti paruoštą kiaušinių plakinį.\n                                                                Kepti pyragą orkaitėje 30 minučių.\n                                                                Patiekti atvėsusį.&lt;/p&gt;', '3832.jpg'),
(11, 'Svogūnų trinta sriuba', '&lt;p&gt;Užkaičiame 1,5 l vandens. Sudedame smulkintą vištieną, morkas ir sultinio kubelį. Verdame, kol išverda vištiena. Tada mėsą atskiriame nuo kaulų ir smulkiais supjaustome. Suberiame smulkintą bulvę, žirnius ir saliero lapus. Taip pat smulkintą vištieną. paskaniname prieskoniais ir sutriname. Papuošiame petražolėmis ir grietine.&lt;/p&gt;', '1518.jpg'),
(12, 'Pieniška sriuba', '&lt;p&gt;Į puodą supilame vandenį, įdedame truputį druskos ir užverdame.&lt;/p&gt;&lt;p&gt;Suberiame makaronus ir verdame apie 20 minučių.&lt;/p&gt;&lt;p&gt;Po to supilame pieną ir kai užverda, nukeliame nuo ugnies.&lt;/p&gt;', '3101.jpg'),
(13, 'Brokolių padažas', '&lt;p&gt;Brokolius išskirstykite smulkesniais žiedynėliais ir virkite pasūdytame vandenyje 10 minučių. Vandenį nupilkite ir sutrinkite brokolius su šakute.&lt;/p&gt;&lt;p&gt;Ištirpinkite keptuvėje sviestą. Sudėkite brokolių košelę, įberkite prieskonių ir išmaišykite. Pakepinkite, maišant, apie 2 minutes. Po to supilkite grietinėlę. Kai užvirs, primažinkite ugnį ir įmaišykite sūrį. Pavirkite dar apie 1 minutę ir nukelkite nuo ugnies.&lt;/p&gt;', '2972.jpg'),
(14, 'Bulvių kukuliai', '&lt;p&gt;Bulves nulupti ir išvirti.&lt;/p&gt;&lt;p&gt;Išvirtas bulves sugrūsti.&lt;/p&gt;&lt;p&gt;Į bulvių košę įmušti kiaušinius, įmaišyti grietinę, miltus ir druską.&lt;/p&gt;&lt;p&gt;Iš bulvių košės suformuoti nedidelius kukulius.&lt;/p&gt;&lt;p&gt;Sudėti į kepimo skardą ir kepti 30-40 min. 180 laipsnių orkaitėje.&lt;/p&gt;&lt;p&gt;Bulvių kukulius patiekti su žalumynų salotomis.&lt;/p&gt;&lt;p&gt;Salotos:&lt;/p&gt;&lt;p&gt;agurkus sumaišyti su svogūnu, žalumynais ir grietine. Paskaninti druska.&lt;/p&gt;', '2149.jpg'),
(15, 'Lašiša su aitriosiomis paprikomis ir avokadų, agurkų salotomis', '&lt;p&gt;Kubeliais supjaustytą avokadą apšlakstykite žaliosios citrinos sultimis. Nulupkite agurką, išimkite sėklas ir supjaustykite gabaliukais, panašiais į avokado kubelius. Sumaišykite visus salotų komponentus ir padėkite į vėsią vietą, kad skonis būtų geresnis.\n                                                                Sumaišykite druską, aitriąsias paprikas ir cukrų. Lašišos filė supjaustykite į 10 gabalėlių ir įtrinkite prieskoniais.\n                                                                Pakaitinkite aliejų ir apie 3 - 4 minutes paskrudinkite lašišą iš abiejų pusių, kol atsiras traški plutelė, o giliau žuvis suminkštės.\n                                                                Patiekite su avokadų ir agurkų salotomis. Taip pat galite patiekti su bulguru arba kuskusu, o šalia paruoškite padažą su aitriosiomis paprikomis CHIPOTLE ir majonezu.&lt;/p&gt;&lt;p&gt;Pabandykite pagardinti lašišą SANTA MARIA CHILI EXPLOSION prieskonių mišiniu!&lt;/p&gt;', '6515.jpg'),
(16, 'Gaivus varškės desertas', '&lt;p&gt;Želatiną sudėkite į pieną ir palikite pusvalandžiui išbrinkti. Tada garų vonelėje kaitinkite pieną, kol granulės ištirps. Atvėsinkite.\n                                                                Į dubenį sudėkite varškę, grietinę, supilkite pieną ir elektriniu plaktuvu išplakite masę. Sudėkite uogas ir riešutus bei pamaišykite. Masę sukrėskite į tortinę ir padėkite į šaldytuvą stingti.&lt;/p&gt;&lt;p&gt;Vietoj riešutų galite naudoti ir trupintus sausainius.&lt;/p&gt;', '6083.jpg'),
(17, 'Lietiniai blynai su krabų lazdelėmis', '&lt;p&gt;Suberkite miltus, druską, įmuškite kiaušinius, pilkite vandens ir pieno, išmaišykite ir iš šios tešlos iškepkite lietinius. Įdarui sūrį sutarkuokite, krabų lazdeles supjaustykite. Susukite įdarą į blynus ir pakepkite.&lt;/p&gt;', '3976.jpg'),
(18, 'Kokteilis „Mėtinė degtinė“', '&lt;p&gt;Mėtas sudėti į taurę, pridėti ledukų, užpilti degtine.&lt;/p&gt;', '1663.jpg'),
(19, 'Zefyrai šokolade', '&lt;p&gt;Susmulkinkite riešutus ir šiek tiek pakepinkite.\n                                                                Pašildykite šokoladą virš verdančio vandens arba mikrobangų krosnelėje (nustatę mažą galią). Įdėkite tarkuotos apelsino žievelės, aitriųjų paprikų ir sumaišykite.\n                                                                Suberkite riešutus ar pasirinktus barstinius į atskiras lėkštes.\n                                                                Medinius krapštukus ar lazdeles maždaug iki pusės įsmeikite į zefyrą. Panardinkite į šokoladą, palaukite, kol nuvarvės, ir pavoliokite į pasirinktus barstinius ar riešutus. Sustatykite aukštoje stiklinėje ar kepimo skardoje, kad sustingtų.&lt;/p&gt;&lt;p&gt;Paruoškite visus zefyrus ir patiekite, kai šokoladas sukietės.&lt;/p&gt;', '6520.jpg'),
(20, 'Moliūgo, pomidoro ir gorgoncolos sūrio druskėtas', '&lt;p&gt;Česnakinio aliejaus gaminimas. Į stiklinę pilti alyvuogių aliejaus, dėti trinto česnako ir išmaišyti.\n                                                                Čiabatos gaminimas. Batoną supjaustyti riekėmis, apšlakstyti trupučiu česnakinio aliejaus, pagardinti druska ir pipirais. Riekes sudėti į kepimo skardą ir dėti kepti į orkaitę, kol gražiai apskrus.\n                                                                Į keptuvę pilti česnakinį aliejų, sudėti kepti pomidorus, barstyti šiek tiek druskos, pipirų, dėti smulkinto česnako. Tada pilti vyno acto, alyvuogių aliejaus, berti truputėlį cukraus ir dar pakepti pora minučių. Iškeptus pomidorus sudėti į dubenį.\n                                                                Į keptuvę pilti alyvuogių aliejaus, suberti smulkintą svogūną, moliūgo kubelius, smulkintą vytintą meksikietišką pipirą, dėti gorgoncolos sūrio gabaliukus, berti truputį druskos, pipirų. Viską kepti, kol moliūgas suminkštės.\n                                                                Ant apskrudusių batono riekių dėti keptus moliūgus ir svogūnus,  pomidorus ir šviežius baziliko lapelius.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '5000.jpg'),
(21, 'Makaronų apkepas su kalakutiena', '&lt;p&gt;Pasūdytame vandenyje apvirkite makaronus 5 minutes. Perdėkite į sietelį, leiskite vandeniui nuvarvėti. Pievagrybius plonai supjaustykite. Pomidorus supjaustykite stambiais kubeliais. Pievagrybius, pomidorus sumaišykite su makaronais ir žirneliais. Perplaukite kalakuto krūtinėlės file šaltu vandeniu, nušluostykite ir supjaustykite kubeliais. Iš visų pusių apkepkite karštame aliejuje (apie 7 minutes). Pasūdykite, įberkite pipirų ir sumaišykite su makaronų mišiniu. Petražoles nuplaukite ir smulkiai supjaustykite. Sumaišykite su grietine, tarkuotu sūriu ir makaronų mase. Įberkite norimų prieskonių. Sudėkite į kepimo formą, išteptą aliejumi. Kepkite orkaitėje apie 30 min.&lt;/p&gt;&lt;p&gt;Apkepas paruoštas!&lt;/p&gt;', '3954.jpg'),
(22, 'Ilgas sumuštinis', '&lt;p&gt;Nedideliame dubenyje sumaišykite baziliką, aliejų ir actą; atidėkite.&lt;/p&gt;&lt;p&gt;Perpjaukite batoną pusiau išilgai. Šaukšto pagalba išgremžkite apatinę batono pusę, palikdami 1 cm storio dugną. Į įdubą įdėkite pusę sūrio, visą jautieną ir visus pomidorus. Ant viršaus supilkite baziliko mišinį; pabarstykite pipirais. Ant viršaus sudėkite likusį sūrį ir viršutinę batono pusę. Suvyniokite į foliją. Pervežkite šaldiklyje.&lt;/p&gt;&lt;p&gt;Įkaitinkite virš laužo ar nedengtame grilyje vidutinėje temperatūroje 20-25 minučių arba kol įkais, apversdami kas 5 minutės. Patiekdami supjaustykite į keturias arba šešias dalis.&lt;/p&gt;', 'no-photo-280x180.jpg'),
(23, 'Amerikietiškas kavos kremas', '&lt;p&gt;Kiaušinių trynius suplakti su cukrumi. Į gautą masę įpilti 0.5 L pieno ir tirpios kavos. Viską gerai suplakti. Įdėti 1 val. į šaldytuvą. Skanaus :)&lt;/p&gt;', '4219.jpg'),
(24, 'Kalėdinis pyragas su avietėmis ir migdolais', '&lt;p&gt;Sviestą  sumaišyti su ruduoju cukrumi ,1 kiaušiniu, medumi, 400 g miltais, kepimo milteliais, cinamonu, druska. Išminkytą tešlą, suvynioti  į foliją, padėti valandai į šaldytuvą. Atšaldytą tešlą iškočioti ir iškloti kepimo formos dugną, o kraštus nupjauti.\n                                                                Įdarui: sviestą (50 g) reikia gerai išsukti, įmaišyti migdolus, po vieną įmušti  2 likusius kiaušinius, galiausiai berkite miltus (50 g). Mase ištepti tešlą. Uogas sudėti į cukrų, išmaišyti ir paskirstyti ant migdolų masės. Jei atsirado sulčių, tai šiek tiek reikia nupilti. Ant viršaus sudėti tešlos juosteles.\n                                                                Kepti apie 20 - 25 minučių 180°C temperatūros karščio orkaitėje. Papuošti migdolų drožlėmis.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '5608.jpg'),
(25, 'Trinta brokolių sriuba', '&lt;p&gt;Salierus ir svogūną susmulkinti. Į puodą supilti aliejų ir pakepinti (2 minutes).&lt;/p&gt;&lt;p&gt;Bulvę nulupti ir supjaustyti kubeliais.&lt;/p&gt;&lt;p&gt;Į puodą sudėti bulves, brokolius, sukapotas petražoles ir prieskonius.&lt;/p&gt;&lt;p&gt;Užpilti sultiniu.&lt;/p&gt;&lt;p&gt;Virti apie 25 minutes.&lt;/p&gt;&lt;p&gt;Išvirusią sriubą sutrinti su elektriniu trintuvu.&lt;/p&gt;&lt;p&gt;Tinka su grietine.&lt;/p&gt;', '2321.jpg'),
(26, 'Karamelinė gira', '&lt;p&gt;Keptuvėje padeginti 500 g cukraus, atskiesti verdančiu vandeniu ir supilti į virintą vandenį, įberti cukrų ir citrinų rūgšties (pagal skonį).\n                                                                Skystį atvėsinti, supilti su cukrumi ir sultimis su trintomis mielėmis.\n                                                                Išmaišyti ir supilstyti į butelius, sandariai uždaryti. Gerti po 2- 3 dienų.&lt;/p&gt;', '4016.jpg'),
(27, 'Bajoriška žuvis su pasninko spirgučiais pagal Radvilų virėjo receptą', '&lt;p&gt;Nuvalytą lydeką supjaustykite, įpilkite aliejaus, užberkite druskos ir pipirų. Taip paruoštą žuvį pamarinuokite pusę valandos.\n                                                                Žuvį kepkite įkaitintoje keptuvėje apie 10 minučių.\n                                                                Pastarnokus supjaustykite kubeliais, pabarstykite druska ir pipirais ir apkepkite.Suraikykite baltą duoną, nupjaukite plutą ir supjaustykite vidutinio dydžio kubeliais. Į įkaitintą keptuvę įpilkite Obelių rapsų aliejaus ir paskrudinkite paruoštus „spirgus“&lt;/p&gt;', '6598.jpg'),
(28, 'Tirštas bananų kokteilis', '&lt;p&gt;Virtuvinio kombaino dubenyje sumaišykite pusę stiklinės atšaldyto pieno, 1 bananą, 1/4 stiklinės jogurto, šaukštelį medaus, 1-2 samtelius vanilinių ledų ir žiupsnelį maltų žemės riešutų. Virtuviniame kombaine viską maišykite tol, kol masė pasidarys vientisa. Ją supilkite į aukštą siaurą stiklinę. Prieš duodami į stalą, į gėrimą įberkite žemės riešutų.&lt;/p&gt;', '1340.jpg'),
(29, 'Bulvių ir kamambero apkepas', '&lt;p&gt;Bulves su lupenomis išvirti, ataušinti, nulupti ir supjaustyti griežinėliais.&lt;/p&gt;&lt;p&gt;Varškę sumaišyti su kiaušiniais, pagardinti.&lt;/p&gt;&lt;p&gt;Kamambero supjaustyti gabaliukais, pomidorus – griežinėliais (aš turėjau sušaldytus pomidorų griežinėlius).&lt;/p&gt;&lt;p&gt;Kepimo formą ištepti aliejumi. Dedame bulves, ant jų pomidorus. Užpilame varškės masę, pabarstome bazilikais ir ant viršaus sudedame kamambero gabaliukus.&lt;/p&gt;&lt;p&gt;Šauname į iki 200-210 laipsnių įkaitintą orkaitę ir kepame 20-25 min.&lt;/p&gt;', '940.jpg'),
(30, 'Žvejybos ant ledo gėrimas', '&lt;p&gt;Visus komponentus sumaišykite ir parą leiskite pastovėti. Perkošti, išpilstyti į butelius. Gerti šaltą.&lt;/p&gt;', '1682.jpg'),
(31, 'Džemas', '&lt;p&gt;Uogas nuplauti, nuskabyti lapelius, didesnes perpjauti per pusę.\n                                                                Braškes sudėti į puodą, užberkite 0,5 kg cukraus. Nuolat maišant kaitinti ant silpnos ugnies, kol užvirs. Suberkite likusį kiekį cukraus, cinamoną ir gerai išmaišykite. Virkite dar 2 minutes.\n                                                                Sudėti į sterilizuotus stiklainius ir uždengite dangteliais.&lt;/p&gt;', '3806.jpg'),
(32, 'Rūtos eglutės', '&lt;p&gt;Ištirpintą sviestą išplakiau su kiaušinių tryniais ir cukrumi.&lt;/p&gt;&lt;p&gt;Po to subėriau kepimo miltelius, supyliau vanilę, aliejų, druską ir dar kartą išmaišiau. Iš lėto subėriau miltus ir išminkiau tešlą.&lt;/p&gt;&lt;p&gt;Tešlą susukau į maistinę plėvelę ir palaikiau šaldymo kameroje apie 1 valandą.&lt;/p&gt;&lt;p&gt;Tešlą iškočiojau ir išspaudžiau su formelėmis eglučių formos sausainius.&lt;/p&gt;&lt;p&gt;Sudėjau sausainius į kepimo popieriumi išklotas kepimo skardas.&lt;/p&gt;&lt;p&gt;Įkaitinau orkaitę iki 180 laipsnių pagal Celsijų ir kepiau apie 10 minučių.&lt;/p&gt;&lt;p&gt;Iškepusius sausainius palikau 15 minučių, kad pravėstų.&lt;/p&gt;&lt;p&gt;Išplakiau kiaušinių baltumus su cukraus pudra. Įlašinau kelis šaukštus maistinių dažų ir aptepiau sausainius. Papuošimui dar užbėriau pabarstukų.&lt;/p&gt;', '2618.jpg'),
(33, 'Kavos kremas', '&lt;p&gt;Grietinėlė su pienu užvirinama, supilama į kavą ir ja užplikomi su cukrumi ištrinti kiaušinių tryniai. Maišant kaitinama, bet neužvirinama. Vėliau į masę supilama ištirpinta želatina. Atvėsintas mišinys išplakamas ir atšaldomas formoje.&lt;/p&gt;', '5802.jpg'),
(34, 'Žaliasis vištienos paštetas', '&lt;p&gt;Kopūstų žiedynėlius sudėti į verdantį sultinį ir virti 8 min. Po to išgriebti, sutrinti maišikliu ir sumaišyti su malta mėsa. Įmušti kiaušinius, suberti miltus, tarkuotą citrinos žievelę, supilti citrinų sultis, sudėti grietinę, pasūdyti, pagardinti pipirais ir gerai išminkyti. Riebalais pateptą pašteto formą iškloti verdančiu vandeniu nuplikytais kopūstų lapais, sudėti faršą ir uždengti likusiais lapais. Indą su paštetu įstatyti į didesnę skardą su vandeniu ir 180 laipsnių karštumo kepti orkaitėje.&lt;/p&gt;', '5174.jpg'),
(35, 'Erškėtrožių uogienė', '&lt;p&gt;Erškėtuoges išdarinėti ir perplauti keliskart pakeičiant vandenį. Po to sumalti, ir apiberti cukrumi. Taip laikyti per naktį, kol atsiras sirupo.&amp;nbsp; Virti ant nedidelės ugnies nuolat maišant apie 30 - 40 min. Jei trūksta, dar įberti cukraus. Dar karštas sudėti į stiklainiukus.&amp;nbsp;Labai geras daiktas žiemą, skaudant gerklytei. Pasidarykit arbatytės :)&lt;/p&gt;', '4090.jpg'),
(36, 'Vištienos ir ryžių troškinys', '&lt;p&gt;Vištienos filė supjaustyti kubeliais ir pakepinti svieste ar aliejuje 10 minučių.&lt;/p&gt;&lt;p&gt;Riebalų reikia tik tiek, kad nesviltų mėsa.&lt;/p&gt;&lt;p&gt;Ryžius virti pasūdytame vandenyje apie 20 minučių. Po to perlieti šaltu vandeniu ir nusausinti.&lt;/p&gt;&lt;p&gt;Papriką supjaustyti juostelėmis ir kartu su sukapotais svogūnų laiškais pakepinti keptuvėje iki 3 minučių.&lt;/p&gt;&lt;p&gt;Į keptuvę sudėti vištieną, ryžius, grybus, supilti sojos padažą ir vyną. Viską išmaišyti ir uždengus dangčiu patroškinti apie 5 minutes.&lt;/p&gt;', '3190.jpg'),
(37, 'Absento kokteilis', '&lt;p&gt;Į stiklinę sudėti ledukus.&lt;/p&gt;&lt;p&gt;Supilti sprite gėrimą ir absentą.&lt;/p&gt;&lt;p&gt;Super skanu!&lt;/p&gt;', '2222.jpg'),
(38, 'Marinuotos paprikos', '&lt;p&gt;Paprikas perpjaukite pusiau. Išimkite sėklializdžius ir sudėkite į stiklainius (prieš tai galima suverti puseles ant medinio pagaliuko - gražiau atrodys).&lt;/p&gt;&lt;p&gt;Cukrų pakaitinkite su vandeniu, kol pagels (karamelizuokite).&lt;/p&gt;&lt;p&gt;Iš lėto supilkite obuolių sultis, baltąjį vyną, actą, suberkite prieskonius, pasūdykite ir palengva maišydami užvirinkite.&lt;/p&gt;&lt;p&gt;Paruoštą užpilą per sietelį atsargiai supilkite į stiklainius su paprikomis.&lt;/p&gt;&lt;p&gt;Sandariai užsukite ir atvėsinkite.&lt;/p&gt;&lt;p&gt;Galima laikyti šaldytuve keletą mėnesių.&lt;/p&gt;&lt;p&gt;Labai tinka prie mėsos ir žuvies patiekalų.&lt;/p&gt;', '4095.jpg'),
(39, 'Karšti sumuštiniai', '&lt;p&gt;Ant duonos riekių kraštų dėti po du šprotus, šonuose sudėti dešros griežinėlius, o ant jų pjaustytus raugintus agurkus. Ant viršaus barstyti tarkuoto sūrio ir kepti orkaitėje, kol sūris gražiai apskrus.&lt;/p&gt;&lt;p&gt;Paprasta, bet labai skanu!&lt;/p&gt;', '5877.jpg'),
(40, 'Saldūs sumuštinukai prie arbatos', '&lt;p&gt;Išplakame kiaušinius, į juos mirkome batono riekeles ir dedame ant įkaitintos keptuvės. Išimame kai gražiai apskrus. Pabarstome cukrumi. Skanaus!&lt;/p&gt;', '1169.jpg'),
(41, 'Burokėlių karpačio su krienų antpilo salotomis bei smulkintomis saulėgrąžomis', '&lt;p&gt;Dubenyje sumaišome krienus, alyvuogių aliejų bei citrinos sultis, į šį antpilą įmaišome ir špinatus. Saulėgrąžas virtuviniu kombainu arba peiliu smulkiai sukapojame. Burokėlį nulupame bei daržovių skustuku pjaustome plonais griežinėliais. Lėkštės viduryje dėliojame burokėlius, juos apšlakstome skystu medumi, ant viršaus dedame špinatų salotas su krienų antpilu, o aplink barstome smulkintas saulėgrąžas. Skanaus!&lt;/p&gt;&lt;p&gt;Išmokite žaliavalgiško maisto gaminimo principų Čiop Čiop žaliavalgystės kursuose: http://www.ciopciop.lt/kursas/zaliavalgyste-31&lt;/p&gt;&lt;p&gt;Daugiau apie Čiop Čiop organizuojamus virtuvės mokymus skaitykite čia: http://www.ciopciop.lt/kursai&lt;/p&gt;&lt;p&gt;Sekite mūsų naujienas Čiop Čiop Facebook puslapyje: http://www.facebook.com/pages/%C4%8Ciop-%C4%8Ciop/173680336006482&lt;/p&gt;', '3519.jpg'),
(42, 'Didysis kriaušių pyragas', '&lt;p&gt;Glazūrai reiks: ištirpintas baltasis šokoladas arba cukraus pudra dekoravimui.&lt;/p&gt;&lt;p&gt;Orkaitę įkaitinkite iki 180°C temperatūros. Ištepkite didelę pyrago formą margarinu. Kriaušes nulupkite ir supjaustykite gabalėliais. Sumaišykite miltus, kepimo miltelius, druską, cinamoną ir muskato riešutą. Suplakite margariną su cukrumi ir toliau plakdami dėkite po vieną kiaušinį. Mediniu šaukštu įmaišykite kriaušių kubelius ir uogas. Pabaigoje suberkite miltų mišinį ir suplakite tešlą į vientisą masę. Sukrėskite į paruoštą formą ir kepkite 1,5 valandos. Leiskite 30 min atvėsti formoje, po to apverskite formą ir pyragą atsargiai išimkite. Papuoškite glazūra arba cukraus pudra.&lt;/p&gt;', '1185.jpg'),
(43, 'Citrinų kisielius', '&lt;p&gt;Nutarkuota citrinų žievelė su cukrumi verdama vandenyje ir iškošiama. Virti apie 15 min.&lt;/p&gt;&lt;p&gt;Bulvių krakmolas ištirpinamas 1 stiklinėje vandens, supilamas į perkoštą skystį, užvirinama ir ataušinama. Supilamos citrinų sultys ir išmaišoma.&lt;/p&gt;', '1708.jpg'),
(44, 'Vaisinės salotos', '&lt;p&gt;Salotas suplėšykite, vaisius supjaustykite juostelėmis, dėkite truputį tarkuoto moliūgo ir sumaišykite su šlakeliu alyvuogių aliejaus.&lt;/p&gt;', '6442.jpg'),
(45, 'Pusryčių bandelės', '&lt;p&gt;Sumaišykite dubenyje grietinėlę, kiaušinius, cukrų ir druską. Ištirpinkite sviestą ir supilkite į grietinėlės masę. Sutrinkite varškę ir taip pat dėkite į grietinėlės masę. Miltus sumaišykite su kepimo milteliais ir suberkite į masę. Minkykite tešlą, kuri turi būti truputį lipni. Uždenkite rankšluosčiu ir palikite 30 minučių pastovėti, tačiau tai nėra būtina, galima kepti iš karto.&lt;/p&gt;&lt;p&gt;Įkaitinkite orkaitę iki 180 C laipsnių temperatūros. Padalykite tešlą į 2 dalis ir tada jas dar padalykite į 10 dalių, gausite 20 bandelių. Miltuotomis rankomis suformuokite bandeles ir dėkite į kepimo popieriumi ištiestą skardą. Bandeles aptepkite grietinėle ir apibarstykite sezamo sėklomis. Leiskite pastovėti 5 minutes.&lt;/p&gt;&lt;p&gt;Bandeles dėkite į orkaitę ir kepkite 20 minučių.&lt;/p&gt;', '1413.jpg'),
(46, 'Teksaso troškinys su aitriosiomis paprikomis', '&lt;p&gt;Ketaus keptuvėje įkaitinę aliejų, apskrudinkite mėsą, vis pavartydami, kad mėsa tolygiai paruduotų. Išimkite mėsą ir pakepkite svogūnus ir česnakus.\n                                                                Vėl sudėkite mėsą į keptuvę ir įdėkite prieskonių, įpilkite sultinio ir alaus. Uždengę dangčiu, ant silpnos ugnies patroškinkite bent dvi valandas. Tada pamaišykite – įsitikinkite, ar skystis neišgaravo ir ar mėsa neprisvilo prie dugno. Jei reikia, įpilkite truputį vandens.\n                                                                Iš pomidorų išimkite sėklas ir juos supjaustykite, taip pat susmulkinkite kalendras ir nuplaukite pupeles.\n                                                                Garnyro produktus sudėkite į atskirą dubenėlį. Patiekalas bus paruoštas mėsai suminkštėjus tiek, kad skaidulos lengvai plyš. Šaukštu suplėšykite mėsą. Pagal skonį pabarstykite druska ir pipirais.&lt;/p&gt;&lt;p&gt;Dėkite troškinį ir garnyrą ant stalo ir patiekite su tortilijomis arba ryžiais.&lt;/p&gt;', '6516.jpg'),
(47, 'Manų košė su obuoliais ir razinomis', '&lt;p&gt;Obuolį išvalyti ir supjaustyti skiltelėmis.&lt;/p&gt;&lt;p&gt;Ištirpinti gabalėlį sviesto ir pakepinti obuolius apie 3-5 minutes.&lt;/p&gt;&lt;p&gt;Baigiant kepinti obuolį sudėti razinas.&lt;/p&gt;&lt;p&gt;Į puodą supilti pieną ir iš lėto įmaišyti manus, druską ir cukrų. Virti, vis pamaišant, apie 20 minučių.&lt;/p&gt;&lt;p&gt;Manų košę patiekti su obuoliais ir razinomis. Į manų košę labai tinka cinamonas. :)&lt;/p&gt;', '3237.jpg'),
(48, 'Varškės apkepas su juodaisiais serbentais', '&lt;p&gt;Serbentus nuplauti, nuskabyti žiedyno liekanas, sutrinu ir užpilti 300g cukraus. Gerai išsukti, kad masė pasidarytų vientisa. Keliolika minučių palaikyti vėsiai. Trynius ištrinti su likusiu cukrumi, įmaišyti pertrintą varškę, supilti pieną, suversti išplaktus baltymus. Į paruošta apkepo indą susluoksniuoti varškę su juodųjų serbentų mase. Kepti 40-45 minutes. Galima valgyti su grietinės arba grietinėlės padažu.&lt;/p&gt;', '4318.jpg'),
(49, 'Sėlenų duona', '&lt;p&gt;Vandenį sumaišyti su pienu ir cukrumi. Mieles ištirpinkite šiame mišinyje.  Sumaišykite miltus su sėlenomis, druska, pridėkite suminkštintą sviestą, supilkite mieles ir minkykite tešlą.  Palikite šiltoje tešlą, kol apimtis padvigubės.  Tešlą dėkite į kepimo formą ir kepkite 200 C laipsnių 1 valandą.&lt;/p&gt;', '1370.jpg'),
(50, 'Triušio kepenėlių paštetas', '&lt;p&gt;Česnako skilteles nulupti, sutrinti ir užpilti stikline aliejaus.\n                                                                Į keptuvę įpilti truputį aliejaus ir dėti  šaukštą sviesto (tuomet sviestas neprisvils). Į ištirpintą sviestą sudėti šalavijo lapus ir apkepti iki rudumo. Šalavijo lapus sudėti ant popierinės servetėlės, kad susigertų riebalų perteklius.\n                                                                Į tą patį aliejų sudėti kepti smulkintą svogūną, dėti truputį česnako, rozmarino šakelę. Šiek tiek apkepus svogūnams, sudėti šoninės kubelius, palaipsniui sudėti kepenėles, įpilti šiek tiek brendžio.\n                                                                Kol kepa kepenėlės gaminamas čibatas. Batoną sulaužyti ir išdėlioti ant kepimo skardos, pabarstyti druskos, apšlakstyti česnakiniu aliejumi ir dėti į įkaitintą orkaitę 5 minutėms (naudoti grilio rėžimą). Apskrudusius batono gabaliukus pabarstyti kietuoju sūriu.\n                                                                Kepenėles mentele pasmulkinti (viduje turi būti kiek rausvos), dėti šaukštą jogurto,  pagardinti druska ir šviežiai maltais pipirais. Keptuvę nuimti nuo ugnies.\n                                                                Kol kepenėlės  vėsta ruošiamas karamelizuotas vynas. Į sausą keptuvę suberti cukrų, supilti vyną ir kaitinti iki vienalytės masės. Sudėti apkepti iš abiejų pusių  slyvų puseles.\n                                                                Kepenėles sumalti virtuviniu kombainu ir paštetu aptepti čibato riekes, ant viršaus dėti karamelizuotas slyvas, skrudintus šalavijo lapelius ir viską apšlakstyti trupučiu aliejaus.&lt;/p&gt;&lt;p&gt;Gero apetito!&lt;/p&gt;', '4824.jpg'),
(51, 'Kokteilis „Šokis su Poema“  (pagal Robertą Janovskį)', '&lt;p&gt;Į maišytuvą įdėkite ledukus ir supilkite visus ingredientus. Plakite tol, kol ledukai ir kiti ingredientai taps vientisa mase. Kokteilį supilkite į šokolado padažu apšlakstytais vidiniais kraštais taurę. Galiausiai kokteilį papuoškite šokoladine plakta grietinėle.&lt;/p&gt;', '3255.jpg'),
(52, 'Putino uogų arbata', '&lt;p&gt;Putino uogas užpilti verdančiu vandeniu, uždengus palaikyti iki 5 min. Gerti su medumi arba cukrumi.&lt;/p&gt;', '1815.jpg'),
(53, 'Salotos ananase', '&lt;p&gt;Kiaušinius išvirti ir supjaustyti kubeliais.\n                                                                Vištieną nuplauti, supjaustyti juostelėmis ir paskrudinti keptuvėje su trupučiu aliejaus.\n                                                                Krevetes išvirti pasūdytame vandenyje su prieskoninėmis žolelėmis ir lauro lapeliu.\n                                                                Sutarkuoti sūrį. Salotas suplėšyti. Dalį ananaso minkštimo supjaustyti kubeliais.\n                                                                Viską sudėti į dubenį, apšlakstyti citrinos sultimis ir išmaišyti su majonezu.\n                                                                Salotas sudėti į išskobtas ananasų puseles. Papuošti pomidoro griežinėliu ir alyvuogėmis.&lt;/p&gt;', '6398.jpg'),
(54, 'Pikantiški krepšeliai su ikrais', '&lt;p&gt;Į įkaitintą orkaitę dėkite apskrusti tešlos krepšelius. Sumaišykite sūrį, kiaušinius, krapus, česnaką ir majonezą. Mase pripildykite krepšelius ir ant viršaus dėkite ikrų.&lt;/p&gt;&lt;p&gt;Skanaus ;)&lt;/p&gt;', '4285.jpg'),
(55, 'Citrinų duona', '&lt;p&gt;Išsijojame miltus su cukrumi, supilame ištirpintą sviestą ar margariną, mušame kiaušinius, šiek tiek druskos, supilame citrinos sultis ir minkome tešlą.&lt;/p&gt;&lt;p&gt;Formuojame kepaliukus ir ištepame juos riebalais ar aliejumi.&lt;/p&gt;&lt;p&gt;Kepame orkaitėje apie 30 min.&lt;/p&gt;&lt;p&gt;Į tešlą galima dėti razinų ir citros žievelių.&lt;/p&gt;&lt;p&gt;Papuošimui naudoti galima avižinius dribsnius.&lt;/p&gt;', '1349.jpg'),
(56, 'Vasariškas alaus kokteilis', '&lt;p&gt;Į didelį ąsotį pilti alų, limonadą, degtinę ir išmaišyti.&lt;/p&gt;&lt;p&gt;Patiekti su ledu, citrinos griežinėliais ir gervuogėmis.&lt;/p&gt;', '3888.jpg'),
(57, 'Salotos su kepenėlėmis', '&lt;p&gt;Kepenis bei morkas išvirti. Svogūnus smulkiai supjaustyti ir apkepti. Grybus stambiai supjaustyti ir taip pat apkepti.\n                                                                Kepenis, morkas bei agurkus supjaustyti plonomis juostelėmis.\n                                                                Viską sumaišyti, pridėti druskos, pipirų bei  majonezo.&lt;/p&gt;', '5635.jpg'),
(58, 'Moliūgų sultys', '&lt;p&gt;Moliūgą, obuolius ir morkas sutarkuoti. Gautą tarkę nusunkti. Jei turite sulčiaspaudę, pasinaudokite ja, bus paprasčiau. :)&lt;/p&gt;&lt;p&gt;Sultis paskaninkite cukrumi.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '1846.jpg'),
(59, 'Traškus kiaulienos kepsnys, marinuotas su Ancho aitriosiomis paprikomis', '&lt;p&gt;Aštriu peiliu kas 5 mm negiliai įpjaukite mėsos paviršių. Sumaišykite visus prieskonius ir įtrinkite jais kiaulieną. Apverskite mėsą ir apipilkite citrinų bei apelsinų sultimis.\n                                                                Apsukite mėsą vyniojamąja plėvele ir marinuokite pernakt.\n                                                                Sekančią dieną kiaulieną padėkite ant folija išklotos kepimo skardos ir uždengę mėsą folija kepkite 200 °C įkaitintoje orkaitėje apie 30 minučių.&lt;/p&gt;&lt;p&gt;Nuimkite foliją ir sumažinkite orkaitės temperatūrą iki 150 °C , kepkite dar apie 1,5 valandos.&lt;/p&gt;&lt;p&gt;Jei kepimo skarda išdžiūna, įpilkite šiek tiek vandens. Norėdami kepsnio su traškia plutele, padidinkite orkaitės temperatūrą iki 225 °C ir apie 10 minučių paskrudinkite, kol kepsnys paruduos.\n                                                                Truputį palaukite ir supjaustykite griežinėliais. Patiekite su keptomis daržovėmis ir persikų&lt;/p&gt;&lt;p&gt;čatniu.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;&lt;p&gt;Patarimas! Išbandykite ir su aitriosiomis paprikomis.&lt;/p&gt;', '6507.jpg'),
(60, 'Kriaušių desertai', '&lt;p&gt;Kriaušes nulupkite ir šiek tiek išskaptuokite, kad tilptų įdaras.&lt;/p&gt;&lt;p&gt;Varškę sumaišykite su sutrintais riešutais, medumi ir vanile.&lt;/p&gt;&lt;p&gt;Varškės įdaro pridėkite į kriaušių puseles.&lt;/p&gt;&lt;p&gt;Kiekvieną kriaušės puselę susukite į foliją.&lt;/p&gt;&lt;p&gt;Kepkite apie 20 minučių 170° C orkaitėje.&lt;/p&gt;', '2856.jpg'),
(61, 'Varškėčiai su klevų sirupu', '&lt;p&gt;Dribsnius sumaišyti su varške, kiaušiniu, dėti cukrų ir sodą ir išminkyti tešlą. Gautą tešlą pabadyti šakute.&lt;/p&gt;&lt;p&gt;Formuoti blynus ir kepti ant mažos ugnies įkaitintuose riebaluose.&lt;/p&gt;&lt;p&gt;Patiekti su klevų sirupu arba medumi.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '3984.jpg'),
(62, 'Batonas su banano įdaru', '&lt;p&gt;Sumaišome varškės sūrį, 1 kiaušinį, cukrų ir miltus. Minkome tešlą, ji turi būti šiek tiek lipni.&lt;/p&gt;&lt;p&gt;Tešlą iškočiojame ir ant jos dedame bananus, o tada suvyniojame.&lt;/p&gt;&lt;p&gt;Aptepame kiaušinio plakiniu, tada tirpintu sviestu ir apibarstome miltais.&lt;/p&gt;&lt;p&gt;Kepame 180-200 laipsnių temperatūroje apie 20 - 30 minučių.&lt;/p&gt;', '1399.jpg'),
(63, 'Blynai', '&lt;p&gt;Atskirame inde išmaišyti sausus produktus.&lt;/p&gt;&lt;p&gt;Atskirai išplakti kiaušinių trynius su pienu ir sviestu (arba aliejumi).&lt;/p&gt;&lt;p&gt;Suberti sausus produktus ir gerai išplakti.&lt;/p&gt;&lt;p&gt;Įmaišyti iki standžių putų išplaktus baltymus.&lt;/p&gt;&lt;p&gt;Kepti įkaitintoje keptuvėje, kol gražiai paruduos.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '3055.jpg'),
(64, 'Varškės apkepas su žemuogėm', '&lt;p&gt;Kiaušinių trynius išsukti iki baltumo su cukrumi, citrinų bei apelsinų žievelėmis. Sukrėsti varškę, suberti manų kruopas, kepimo miltelius. Tešlą gerai išmaišyti. Suberti paruoštas žemuoges. Atsargiai pamaišyti. Baltymus išplakti iki standžių putų. Įmaišyti į varškės kremą. Paruoštą tešlą sukrėsti į riebalais pateptą ir miltais pabarstytą kepimo formą. Kepti 200 laipsnių temperatūros įkaitintoje orkaitėje gerą pusvalandį. Tiekiant į stalą, pabarstyti vanilinio cukraus pudra.&lt;/p&gt;', '4313.jpg'),
(65, 'Pomidorinė kopūstų sriuba', '&lt;p&gt;Puode užkaičiamas vanduo. Kopūstai susmulkinami ir metami į puodą. Supjaustoma kiauliena, bulvės, morkos ir petražolės, sudedama į puodą. Verdama 30 minučių. Likus 5 minutėms iki virimo pabaigos sudedami smulkinti pomidorai. Svogūnai susmulkinami ir apkepinami keptuvėje, tada sudedama į sriubą. Taip pat beriama druska ir pipirai.&lt;/p&gt;&lt;p&gt;Prieš patiekiant apibarstoma krapais.&lt;/p&gt;', '1572.jpg'),
(66, 'Karštas raudonas vynas', '&lt;p&gt;Supilkite vyną į puodą; sudėkite gvazdikėlius, cinamoną, cukrų, migdolus, razinas ir apelsino žievelę.&lt;/p&gt;&lt;p&gt;Ant nedidės ugnies įkaitinkite, bet neužvirkite.&lt;/p&gt;&lt;p&gt;Jei norite, supilkite degtinę; perkoškite ir patiekite karštą.&lt;/p&gt;', '1621.jpg'),
(67, 'Juodų serbentų uogienė', '&lt;p&gt;Uogas nuplauti, išmaišyti su fruktoze arba cukrumi, pridengti ir palikti pastovėti kelias valandas, kad išsiskirtų sultys. Toliau užvirinti ant mažos ugnies ir virti nepridengus, kol kiek sutirštės. Jei susidarys putos, jas reikia nugriebti. Karštą uogienę išpilstyti į švarius, karštus, sterilizuotus stiklainius, uždaryti, pastatyti aukštyn kojomis.&lt;/p&gt;', '4279.jpg'),
(68, 'Kepta manų košė su pasukomis', '&lt;p&gt;Suplakti kiaušinius su cukrumi. Įmaišyti sodą su actu.\n                                                                Sumaišyti su  mišiniu visus kitus komponentus ir palikti 30 minučių brinkti.\n                                                                Kepti 180 °C įkaitintoje orkaitėje 45- 50 minučių.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '4175.jpg'),
(69, 'Itališka kopūstų sriuba su grūdais', '&lt;p&gt;Svogūnus susipjaustyk smulkiai. Kopūstus taip pat susipjaustyk. Nutarkuok citrinos žievelę, tik geltoną dalį.&lt;/p&gt;&lt;p&gt;Svogūnus pakepink svieste kartu su citrinos žievele ir petražolėmis. Kepinama maišant 2 minutes.&lt;/p&gt;&lt;p&gt;Suberk kmynus ir miežius, gerai išmaišyk ir dar pakepink pusė minutės, o tada sudėk į verdantį sultinį.&lt;/p&gt;&lt;p&gt;Į puodą taip pat sudėk kopūstus ir virk apie 25 minutes. Paskanink druska ir pipirais.&lt;/p&gt;&lt;p&gt;Saulėgrąžos pakepinamos keptuvėje ir prieš tiekiant jomis apibarstoma sriuba. Taip pat apibarstoma petražolėmis.&lt;/p&gt;&lt;p&gt;Skanaus&lt;/p&gt;', '1573.jpg'),
(70, 'Šokoladiniai saldainiai', '&lt;p&gt;Padarykite želė ir padėkite stingti į šaldytuvą. Kai jau bus kiek sustingusi (klampi) pradėkite gaminti saldainius.\n                                                                Vaflių lakštus pertepkite plonu sluoksniu &quot;Rududu&quot; kremu. Kiekvieną kartą pertepus, pabarstykite smulkintų riešutų (pakepintų sausoje keptuvėje). Supjaustykite vaflius kubeliais (5 X 5).\n                                                                Pamerkite juos klampioje želė masėje, sudėkite į lėkšte ir padėkite į šaldytuvą valandai. Tada kiekvieną saldainuką (naudojant arbatinį šaukštelį) pamerkite į lydytą (kiek įmanoma labiau atvėsusį) šokoladą, sudėkite į lėkšte ir valandai padėkite į šaldytuvą.&lt;/p&gt;&lt;p&gt;Skanaus ;)&lt;/p&gt;', '5526.jpg'),
(71, 'Sulos kokteilis', '&lt;p&gt;Viską išplakame ir supilstome į taures. Papuošti galima žaliosios citrinos griežinėliu.&lt;/p&gt;', '1669.jpg'),
(72, 'Karštos vištienos ir cukinijų  salotos', '&lt;p&gt;Vištiena pabarstoma kariu, pipirais, druska, apšlakstoma citrinų sultimis ir pakepinama įkaitintame alyvuogių aliejuje. Cukinija supjaustoma griežinėliais ir atskiroje keptuvėje pakepinama iš abiejų pusių kartu su paprika, o baigiant kepti apkepamas ir smulkiai pjaustytas poras. Patiekiama su argula žolelėmis.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '4209.jpg'),
(73, 'Baklažanų blynai', '&lt;p&gt;Baklažanų minkštimą supjaustyti kubeliais ir kepti 160-180 laipsnių orkaitėje apie 25 minutes.&lt;/p&gt;&lt;p&gt;Keptą baklažanų minkštimą sutrinkite šakute ir sumaišykite su sumaltais graikiniais riešutais, kiaušiniu, grikių ir kvietiniais miltais, kepintais svogūnais ir grybais, prieskoniais ir sutrintu česnaku.&lt;/p&gt;&lt;p&gt;Kepti įkaitintame aliejuje plonus blynelius. 2 šaukštai tešlos - 1 blynas. Apkepkite iš abiejų pusių, kol gražiai pagels.&lt;/p&gt;', '3481.jpg'),
(74, 'Vafliniai blyneliai su vyšnių padažu', '&lt;p&gt;Per sietelį persijoti sumaišytus miltus, kepimo miltelius ir druską. Kitame dubenyje ištrinti kiaušinių trynius su cukrumi, kol cukrus visiškai ištirps ir masė taps gelsvai balta. Supilti romo esenciją ir vanilės sirupą, ištirpdytą sviestą ir pieną bei gerai išmaišyti.&lt;/p&gt;&lt;p&gt;Visą šią skystą masę pilti į miltus ir maišyti, kol neliks miltų gumulėlių (darbui palengvinti galite naudoti elektrinį plaktuvą). Iki purumo suplakti kiaušinių baltymus ir juos atsargiai įmaišyti į tešlą. Nepermaišyti! Dėti samčiu į širdelės formos vaflinę ar blynų keptuvę ir kepti, kol gražiai apskrus.&lt;/p&gt;&lt;p&gt;Vyšnių padažas gaminimas:&lt;/p&gt;&lt;p&gt;Indelyje sumaišykite  pusę kiekio vandens su krakmolu. Kitame inde ant viryklės ištirpinkite sviestą, sudėti vyšnias, supilti likusį vandenį ir rudąjį cukrų. Maišykite, kol cukrus ištirps. Supilkite vandens ir krakmolo mišinį, maišykite, kol masė užvirs ir sutirštės (~1 min).&lt;/p&gt;&lt;p&gt;Nukelkite nuo ugnies ir įmaišykite citrinos sultis. Tiekti su vafliniais blynais.&lt;/p&gt;', '1287.jpg'),
(75, 'Sumuštinukai', '&lt;p&gt;Duonos riekeles ištepti sviestu.&lt;/p&gt;&lt;p&gt;Sutarkuoti sūrį ir užberti ant kiekvienos duonos riekelės.&lt;/p&gt;&lt;p&gt;Graikinius riešutus sumalti ir išmaišyti su sugrūstu česnaku ir majonezu. Sūrį aptepti majonezu ir ant viršaus uždėti kivi griežinėlius.&lt;/p&gt;', '2082.jpg'),
(76, 'Obuolių pyragas su riešutais', '&lt;p&gt;Obuolius išvalome, išpjauname sėklalizdžius. Į obuolius priberiame cukraus, smulkintų graikinių riešutų ir sudedame į kepimo skardą.\n                                                                Kiaušinius išplakame su likusiu cukrumi, įmaišome grietinę, tirpintą sviestą, sodą ir miltus.\n                                                                Supilame tešlą tarp obuolių. Apibarstome riešutais ir kepame 180 laipsnių orkaitėje 40 minučių.&lt;/p&gt;', '3475.jpg'),
(77, 'Įdaryti vafliai', '&lt;p&gt;Kiaušinius gerai išplakite su cukrumi. Ištirpinkite margariną. Sumaišykite plaktus kiaušinius, margariną ir miltus.\n                                                                Įkaitinkite vaflių keptuvę ir plonai ištepkite margarinu. Kepkite vaflius ir susukite į vamzdelius.\n                                                                Elektriniu plaktuvu suplakite kondensuotą &quot;Rududu&quot; pieną ir suplaktus kiaušinių baltymus. Įdaru užpildykite vaflių vamzdelius.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '4055.jpg'),
(78, 'Citrinų skonio keksiukai su aguonomis', '&lt;p&gt;Išplakti kiaušinius su cukrumi.&lt;/p&gt;&lt;p&gt;Įmaišyti margariną, vanilinį cukrų, aguonas, sodą. Įspausti citrinos sultis ir įmaišyti miltus.&lt;/p&gt;&lt;p&gt;Keksiukų tešlą supilstyti į keksiukų formeles.&lt;/p&gt;&lt;p&gt;Kepti 180° C orkaitėje 15-20 minučių.&lt;/p&gt;', '2059.jpg'),
(79, 'Kriaušėmis įdarytos vištos blauzdelės', '&lt;p&gt;Atsargiai išimkite kaulus iš blauzdelių ir ištrinkite jas druska bei pipirais. Kriaušes nulupkite, supjaustykite griežinėliais, sumaišykite su riešutais bei mase įdarykite blauzdeles. Dantų krapštukais perverkite  blauzdelių galus. Įdarytas blauzdeles sudėkite į kepimo rankovę, ant viršaus sudėkite žiedais pjaustytus svogūnus ir sandariai uždarykite rankovę. Kepkite 200 °C temperatūros orkaitėje 1 valandą.&lt;/p&gt;', '6007.jpg'),
(80, 'Kriaušių pyragas', '&lt;p&gt;Iš sviesto, baltojo cukraus, kiaušinio, vanilinio cukraus, miltų ir kepimo miltelių užmaišykite tešlą.&lt;/p&gt;&lt;p&gt;Sudėkite 3/4 tešlos į kepimo formą. Ant tešlos sudėkite kubeliais supjaustytas kriaušes, sumaišytas su cinamonu ir ruduoju cukrumi.&lt;/p&gt;&lt;p&gt;Sudėkite ant kriaušių likusią tešlą.&lt;/p&gt;&lt;p&gt;Kepkite pyragą 180 laipsnių orkaitėje apie 35 minutes.&lt;/p&gt;', '3407.jpg'),
(81, 'Makaronų troškinys su alyvuogėmis', '&lt;p&gt;Makaronus išvirti pasūdytame vandenyje su trupučiu aliejaus. Tada nusunkti skystį ir sudėti pomidorų pastą, tarkuotą kietąjį sūrį, alyvuoges, smulkintas petražoles ir išmaišyti.&lt;/p&gt;&lt;p&gt;Skanu ir paprasta!&lt;/p&gt;', '6128.jpg'),
(82, 'Blyneliai', '&lt;p&gt;Šie blyneliai labai gardūs ir kepami visiškai be riebalų. Kadangi kepame visiškai be aliejaus, jie tolygiai įgauna auksinį atspalvį.&lt;/p&gt;&lt;p&gt;Kiaušinius mikseriu suplakti su cukrumi, kol masė pasidarys kaip kremas su putele. Tuomet pilti pieną ir vėl viską išplakti. Galiausiai dėti miltus tol, kol tešla pasidarys gana skysta. Keptuvę tereikės truputėli pakaitinti ir su šaukštu dėti tešlos. Vienoje vidutinio dydžio keptuvėje turėtų tilpti 3 apvalūs blyneliai.&lt;/p&gt;&lt;p&gt;Patarimas:&lt;/p&gt;&lt;p&gt;Blynelius reikia apversti kai kraštai pradeda geltonuoti, jei lauksite ilgiau jie tiesiog apsvils degėsiais. Labai skanu su uogiene, grietine ir cukrumi, marmaledu, karamele... Jie labai gardūs ir vieni!&lt;/p&gt;', '4397.jpg'),
(83, 'Sluoksniuotos tešlos pyragėliai su įdaru', '&lt;p&gt;Sluoksniuotos tešlos lakštus supjaustau 10 cm kvadratėliais.\n                                                                Turėjau tik šaldytas, tai uogas sutryniau. Įmaišiau į trintas uogas cukraus (pagal skonį). Tiesa, uogas tryniau atskirai.\n                                                                Ant kiekvieno kvadratėlio uždėjau įdaro ir užlanksčiau kraštus į vidų arba uždėjau ant įdaro neapteptą tešlos kvadratėlį ir kraštus užspaudžiau.&lt;/p&gt;&lt;p&gt;Apibarsčiau pyragėlius cukrumi.\n                                                                Kepiau apie 20-25 minutes 190° C laipsnių orkaitėje.&lt;/p&gt;', '3118.jpg'),
(84, 'Slyvų kompotas', '&lt;p&gt;Prinokusios kietos slyvos nuplaunamos, perplaunamos pusiau ir, išėmus kaulelius, sudedamos į stiklainius. Iš cukraus ir vandens išverdamas sirupas, atšaldomas ir, užpylus ant slyvų, pridengus metaliniais dangteliais, 1 L stiklainiai 85° C temperatūroje kaitinami 20 min. kaitinamo vandens puode. Po to indai hermetiškai uždaromi.&lt;/p&gt;', '4039.jpg'),
(85, 'Gardūs vištienos piršteliai', '&lt;p&gt;File nuplauti , nusausinti ir supjaustyti juostelėmis.\n                                                                Kiaušinį išplakite ir jame apvoliokite vištienos juosteles.\n                                                                Tada apvoliokite prieskoniuose ir kepkite įkaitintame aliejuje, apvečiant iš kelių pusių.&lt;/p&gt;', '3811.jpg'),
(86, 'Putojančio vyno ir sulčių punšas', '&lt;p&gt;Supilame sultis,gazuotą vandenį, putojantį vyną ir suberiame cukrų. Išmaišome. Po to suberiame ledukus.&lt;/p&gt;&lt;p&gt;Papuošimui tinka įvairūs vaisiais.&lt;/p&gt;', '3559.jpg'),
(87, 'Džiovintų kriaušių kompotas', '&lt;p&gt;Džiovintos kriaušės nuplaunamos šiltu vandeniu, užpilamos šaltu ir išverdamos. Vėliau išgriebiamos į dubenėlį.&lt;/p&gt;&lt;p&gt;Į jų nuovirą suberiamas cukrus, prieskoniai, viskas pavirinama, iškošiama ir galima skanauti. :)&lt;/p&gt;', '1710.jpg'),
(88, 'Brokolių blyneliai', '&lt;p&gt;Brokolius užpilkite verdančiu vandeniu ir palaikykite 15 minučių.&lt;/p&gt;&lt;p&gt;Brokolius sutrinkite.&lt;/p&gt;&lt;p&gt;Į brokolių masę įmaišykite kiaušinius, miltus, pasukas, sodą ir druską.&lt;/p&gt;&lt;p&gt;Iš šios masės kepkite blynus.&lt;/p&gt;&lt;p&gt;Kepkite ištirpinto sviesto masėje, kol abi blyno pusės apskrus. :)&lt;/p&gt;', '2800.jpg'),
(89, 'Makaronai Ta gliatelle su aitriosiomis paprikomis ir česnakais', '&lt;p&gt;Išvirkite makaronus, kaip rekomenduojama ant pakuotės. Pakaitinkite aliejų, įdėkite česnakų, aitriųjų paprikų dribsnių ir keletą minučių pakepkite, bet neprideginkite česnakų!\n                                                                Sudėkite pomidorų puseles ir pakaitinkite dar 1 - 2 minutes.\n                                                                Sudėkite virtus bei nusunktus makaronus ir kaitinkite 1–2 minutes.&lt;/p&gt;&lt;p&gt;Patiekite su parmezano sūriu ir šviežiomis petražolėmis.&lt;/p&gt;&lt;p&gt;Patarimas! Makaronai idealiai tinka prie vištienos suktinukų su aitriųjų paprikų CHIPOTLE ir pesto padažo įdaru!&lt;/p&gt;', '6509.jpg'),
(90, 'Bananų blynai', '&lt;p&gt;Išplakite kiaušinius su pienu, druska ir cukrumi.&lt;/p&gt;&lt;p&gt;Įmaišykite miltus.&lt;/p&gt;&lt;p&gt;Įkaitinkite keptuvėje 1 šaukštą aliejaus ir kepkite blynus. Pilkite 2 šaukštus tešlos ir ant tešlos sudėkite bananų griežinėlius. Apkepkite blynus iš abiejų pusių, kol gražiai pagelsta.&lt;/p&gt;&lt;p&gt;Padažas: išspaustas apelsinų sultis supilkite į puodelį ir užkelkite ant ugnies. Įmaišykite cukrų ir žieveles. Kai cukrus ištirps, įmaišykite romą ir nukelkite nuo ugnies.&lt;/p&gt;&lt;p&gt;Blynus patiekite su padažu. :)&lt;/p&gt;', '2857.jpg'),
(91, 'Arbata su vanile', '&lt;p&gt;Vandenį užviriname su vanilės lazdele, po to vanilę išimame.&lt;/p&gt;&lt;p&gt;Vandeniu užpilame arbatžoles.&lt;/p&gt;', '1732.jpg');
INSERT INTO `recipes` (`id`, `name`, `describtion`, `image_name`) VALUES
(92, 'Lašiša įdaryti kiaušiniai', '&lt;p&gt;Išvirkite kietus kiaušinius (virti apie 10 min.), didelius kiaušinius reikia virti ilgiau.&lt;/p&gt;&lt;p&gt;Nulupti ir perpjauti per pusę. Trynius išimti ir sumaišyti su smulkinta lašiša, krapais ir majonezu. Pridėti pasigaminto įdaro į kiaušinių baltymų puseles.&lt;/p&gt;&lt;p&gt;Greita ir skanu :)&lt;/p&gt;', '3342.jpg'),
(93, 'Uogų sirupo kisielius', '&lt;p&gt;Į dvi stiklines karšto vandens supilti sirupą (vyšnių, braškių ar juodųjų serbentų), įberti cukrų ir šiek tiek citrinos rūgšties, ištirpintos nedideliame kiekyje vandens. Visa tai gerai maišant užkaitinti, supilti vienoje stiklinėje vandens išleistą krakmolą ir vėl pakaitinti iki virimo.&lt;/p&gt;', '4035.jpg'),
(94, 'Gervuogių lapų arbata', '&lt;p&gt;Jaunus lapus apvytinkite ore, po to pabaikite džiovinti orkaitėje 40 C temperatūroje, smulkiai supjaustykite ir laikykite uždaruose popieriniuose paketuose.&lt;/p&gt;&lt;p&gt;Užplikykite lapus kaip arbatą, pasaldinkite ir gerkite.&lt;/p&gt;', '1718.jpg'),
(95, 'Sūrainiai "Sūrskiai"', '&lt;p&gt;Bandeles perpjauname per pusę,apatinę dalį patepame majonezu, uždedame pakepintą hamburgerio mėsytę, uždedame pora riekelių pomidoro ir lapelį sūrio. Uždengiam bandelės viršumi ir dedame į orkaitę 5 min.&lt;/p&gt;', '4262.jpg'),
(96, 'Bakalieno Tinginys', '&lt;p&gt;Galima daryti paprastą, taip pat nedėti razinų, jei nemėgstate, beto jas reikia susmulkinti, kad liptų su kitais ingridientais.&lt;/p&gt;&lt;p&gt;Galima dėti netgi datulių ar figų, taip pat susmulkinus. O riešutus geriau palikti stanbesnius, kitaip nelabai jausis skonis. Sulaužome sausainius. Susmulkiname stambias razinas, bei riešutus (galima ir peiliu, arba subėrus į indą patrinti su bulvių trintuve, arba įdėjus į medžiaginį maišelį pasmulkinti duodant per jį sunkesniu daiktu) ir suberiame viską ant sausainių. Sviestą ištirpiname inde ant ugnies (neužverdame), supilame kondensuotą pieną, gerai išmaišome, įberiame kakavos, išmaišome.&lt;/p&gt;&lt;p&gt;Dar pakaitinę nuimame nuo ugnies ir pilame ant sausainių. Gerai išmaišome ir dedame į kepimo rankovę, formuojame.&lt;/p&gt;&lt;p&gt;Dedame į šaldytuvą, sustingusį tiekiame.&lt;/p&gt;&lt;p&gt;Skanaus !&lt;/p&gt;', '4416.jpg'),
(97, 'Moliūgų pyragas su grietinėlės glaistu', '&lt;p&gt;Kvietinius miltus sumaišykite su kepimo milteliais, maltu muskatu, imbieru ir cinamonu.\n                                                                Kiaušinį išplakite su medumi, ištirpintu sviestu ir moliūgų koše.\n                                                                Įmaišykite apelsino žievelę ir avižinius dribsnius. Po to įmaišykite miltų mišinį.\n                                                                Tešlą supilkite  kepimo formą ir kepkite 45 minutes 180° C orkaitėje.&lt;/p&gt;&lt;p&gt;Po to pyragą aptepkite cukraus pudros, grietinėlės sūrio ir apelsinų sulčių plakiniu.&lt;/p&gt;&lt;p&gt;Skanaus! :)&lt;/p&gt;', '3482.jpg'),
(98, 'Cukinijos ir maltos mėsos apkepas', '&lt;p&gt;Smulkiai supjaustykite svogūną. Morkas sutarkuokite.&lt;/p&gt;&lt;p&gt;Pakepinkite svogūną, kol gražiai pagels (~3 min.). Sudėkite morkas, faršą, prieskonius ir kepinkite 15-20 minučių.&lt;/p&gt;&lt;p&gt;Kubeliais supjaustykite cukiniją ir pakepinkite atskiroje keptuvėje apie 5-10 min.&lt;/p&gt;&lt;p&gt;Sumaišykite kepintą mėsą su cukinijomis ir sudėkite į kepimo skardą.&lt;/p&gt;&lt;p&gt;Apibarstykite sūriu ir kepkite 20 minučių 180 laipsnių karštumo orkaitėje.&lt;/p&gt;', '3358.jpg'),
(99, 'Putojančio vyno–žagarės kokteilis', '&lt;p&gt;Išmaišyti, pridėti ledukų ir ragauti. Skanaus!&lt;/p&gt;', '1673.jpg'),
(100, 'Mėtų limonadas', '&lt;p&gt;Mėtas užpilame verdančiu vandeniu. Įmaišome cukrų, sultis.&lt;/p&gt;&lt;p&gt;Geriame atšaldytą.&lt;/p&gt;', '1742.jpg'),
(101, 'Gardučiai keksiukai', '&lt;p&gt;Sumaišykite miltus su kepimo milteliais, sudėkite likusius komponentus (išskyrus mėlynes) ir išplakite tešlą.\n                                                                Į tešlą suberkite mėlynes ir išmaišykite.\n                                                                Tešlą sudėkite į keksiukų formeles, pateptas aliejumi. Kepkite 160 °C temperatūros orkaitėje 20 minučių.&lt;/p&gt;', '6453.jpg'),
(102, 'Sūrio kroketai', '&lt;p&gt;Suplakite kiaušinio baltymus. Sutarkuokite sūrį ir užpilkite plaktais kiaušinių baltymais. Iš gautos masės padarykite nedidelius kamuoliukus ir užšaldykite.\n                                                                Kiaušinio trynį lengvai išplakti šakute.\n                                                                Šaldytus kroketus apvolioti džiūvėsėliuose, tada volioti plaktame trynyje ir vėl džiūvėsėliuose. Kepti 1 - 2 minutes.\n                                                                Patiekti su grietine ir šparaginėmis pupelėmis.&lt;/p&gt;', '6303.jpg'),
(103, 'Kiaulienos troškinys', '&lt;p&gt;Kiaulieną supjaustykite nedideliais gabaliukais. Ištirpinkite sviestą ir pakepinkite mėsą apie 5 minutes, vis apverčiant.&lt;/p&gt;&lt;p&gt;Po to sudėkite susmulkintus svogūnus ir česnaką. Pakepinkite dar 2 minutes.&lt;/p&gt;&lt;p&gt;Sudėkite kubeliais supjaustytus agurkus, pomidorus.&lt;/p&gt;&lt;p&gt;Sumaišykite grietinę su pomidorų pasta, garstyčiomis, sojos padažu ir prieskoniais. Šį padažą supilkite į keptuvę.&lt;/p&gt;&lt;p&gt;Viską išmaišykite ir patroškinkite apie 15 minučių.&lt;/p&gt;', '2813.jpg'),
(104, 'Įdarytų paprikų užkandis', '&lt;p&gt;Nupjauti paprikų viršūnę ir išimti sėklas. Smulkiais kubeliais supjaustyti pomidorus. Juostelėmis supjaustyti agurkus. Avokadą nulupti ir sutrinti smulkintuvu. Svogūnus ir likusius žalumynus sukapoti. Viską sumaišyti ir mase įdaryti paprikas.&lt;/p&gt;', '6011.jpg'),
(105, 'Skani bulvių košė', '&lt;p&gt;Nulupti ir išvirti bulves pasūdytame vandenyje.\n                                                                Smulkiai supjaustyti svogūnus ir apkepinti svieste. Išplakti kiaušinį ir sumaišyti su kepintais svogūnais.\n                                                                Pakaitinti grietinėlę, bet ne virti.\n                                                                Grūsti bulves kartu su grietinėle ir svogūnų mase. Iš košės šaukštu formuoti bulvių košę.&lt;/p&gt;&lt;p&gt;Valgyti kaip pagrindinį patiekalą arba patiekti kaip garnyrą.&lt;/p&gt;&lt;p&gt;Skanaus&lt;/p&gt;', '4142.jpg'),
(106, 'Vasariški šaltibarščiai', '&lt;p&gt;Nuplauti supjaustyti burokėliai išvedriami mažame kiekyje parūgštinto vandens. Ridikėliai supjaustomi griežinėliais, o agurkai juostelėmis (be sėklų). Kefyras, sumaišius su grietine, išplakamas mediniu šaukštu, sudedama kiaušinis, žalumynai ir viskas sumaišoma su virtais burokėliais. Palaikoma šaltai, kad atvėstų.&lt;/p&gt;', '5824.jpg'),
(107, 'Netikras Zuikis kitaip', '&lt;p&gt;Į didelį indą sudedame faršą, įmušame kiaušinius, išspaudžiame česnako skilteles, dedame prieskonius  ir viską gerai išmaišome.\n                                                                Susipjaustome kūbeliais pievagrybius ir svogūną, susitarkuojame morką ir viską beriame į įkaitintą keptuvę su aliejumi, masę pagardiname druska ir pipirais. Viską pakepiname.\n                                                                Imame pailgą kepimo formą ir  dugną paklojame maždaug  3 cm storio faršo sluoksniu.\n                                                                Supilame pakepintas daržoves ir tolygiai paskirstome antrą sluoksnį.\n                                                                Likusį faršą dedame ant viršaus ir taip pat tolygiai paskirstome, suformuodami trečią sluoksnį.\n                                                                Formą pašauname į  įkaitintą orkaitę ir kepame 200°C temperatūroje apie 30 – 40 min.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;&lt;p&gt;P.S. Dekoracijai pasirinkau alyvuoges. Prieš dėdama pirmąjį faršo sluoksnį, formoje gražiai išdėsčiau alyvuoges ir tik tada pradėjau sluoksniavimą.&lt;/p&gt;', '3818.jpg'),
(108, 'Netradiciniai šaltibarščiai su špinatais', '&lt;p&gt;Į gilų dubenį supilame kefyrą ir pasukas. Burokėlius į dubenį pilame su visomis. Sukratome smulkintą agurką ir kapotus kiaušinius. Salotas ir špinatus supjaustome ir dedame į šaltibarščius. Viską galima sutrinti smulkintuvu. Viską pagardiname druska. Valgome su virtomis bulvėmis.&lt;/p&gt;', '5816.jpg'),
(109, 'Vengriška šalta sriuba', '&lt;p&gt;Obuolius nulupti, išimti sėklalizdžius, supjaustyti kubeliais. Suberti į puodą, užpilti vandeniu ir virti, kol suminkštės.&lt;/p&gt;&lt;p&gt;Tada supilti pieną, cinamoną ir vyną.&lt;/p&gt;&lt;p&gt;Grietinę išmaišyti su miltais ir supilti į sriubą.&lt;/p&gt;&lt;p&gt;Užvirti ir nukelti nuo ugnies.&lt;/p&gt;&lt;p&gt;Atvėsinti, įdėti į šaldytuvą.&lt;/p&gt;&lt;p&gt;Pagardinti citrinos sultimis.&lt;/p&gt;&lt;p&gt;Patiekti su sausainiais, krekeriais.&lt;/p&gt;', '4227.jpg'),
(110, 'Grūdėtos varškės ir avokado sumuštinukai', '&lt;p&gt;Visus produktus išmaišome. Gautą masę ir tepame ant juodos duonos riekelių.&lt;/p&gt;', '6286.jpg'),
(111, 'Bulviniai blynukai', '&lt;p&gt;Bulves nuskusti, sutarkuoti, įdėti druskos ir pipirų, įmušti kiaušinį, viską išmaišyti. Užplikyti pieno stiklinę ir užpilti ant bulvių masės, išmaišyti ir kepti.&lt;/p&gt;&lt;p&gt;Kiekvieną blyno pusę kepti apie 2 minutes.&lt;/p&gt;', '2947.jpg'),
(112, 'Mišrainė iš žalių pomidorų', '&lt;p&gt;Pomidorus, svogūnus, morkas supjaustome, apibarstome druska ir paliekame kambario temperatūroje 12 val. Ruošiame marinatą. Užviriname aliejų ir actą su cukrumi bei prieskoniais. Į jį sudedame daržoves ( be skysčio) ir troškiname 30 - 35 min. Viską sudedame į iškaitintus stiklainius ir uždarome.&lt;/p&gt;', '5307.jpg'),
(113, 'Braškinis brendžio ir vyno gėrimas', '&lt;p&gt;Braškes sudėti į didelį ąsotį.&lt;/p&gt;&lt;p&gt;Užpilti cukrumi ir pilti brendį. Laikyti  8 valandas šaldytuve.&lt;/p&gt;&lt;p&gt;Tada supilti  atšaldytą vyną.&lt;/p&gt;&lt;p&gt;Prieš patiekiant į taures įdėti keletą braškių.&lt;/p&gt;', '3931.jpg'),
(114, 'Persikų čapnis', '&lt;p&gt;Nulupkite persikus ir supjaustykite gabalėliais.\n                                                                Nulupkite svogūną ir supjaustykite griežinėliais.\n                                                                Visus produktus sudėkite į puodą ir kaitinkite 5 - 7 minutes. Jei norite aitrumo, įberkite aitriųjų paprikų dribsnių.\n                                                                Paruoštą čatnį laikykite šaldytuve.&lt;/p&gt;&lt;p&gt;Patarimas! Čatnis idealiai tinka prie ant grotelių ar orkaitėje keptos mėsos, žuvies ir įvairių rūšių sūrio.&lt;/p&gt;', '6502.jpg'),
(115, 'Saldus surprizas', '&lt;p&gt;Įpilkite pieną į stiklinę, kad apsemtų daugiau nei pusė stiklinės.\n                                                                Po to įpilkite šokoladinio sirupo (pagal jūsu skonį).\n                                                                Pilkite šiek tiek grietinėlės.&lt;/p&gt;', '4797.jpg'),
(116, 'Margaritos varškės apkepas', '&lt;p&gt;1. Sumaišyti visus produktus. Cukraus galima dėti ir daugiau – kaip patinka.&lt;/p&gt;&lt;p&gt;Palikti 5-7 min. pastovėti.&lt;/p&gt;&lt;p&gt;2. Kepimo indą ištepti sviestu.&lt;/p&gt;&lt;p&gt;3. Apkepą dėti į iki 200 laipsnių įkaitintą orkaitę ir kepti 30-35 min.&lt;/p&gt;', '1083.jpg'),
(117, 'Duonos giros kisielius', '&lt;p&gt;Duonos girą iškošti, supilti į neoksiduojantį indą, įpilti cukrų, įmesti citrinos žievės, pakaitinti iki 80°C, supilti išleistą krakmolą, pakaitinti iki virimo, nuimti nuo ugnies, supilstyti į indus ir atvėsinti.&lt;/p&gt;', '4032.jpg'),
(118, 'Blynai be kiaušinių', '&lt;p&gt;Į jogurtą įmaišykite žiupsnelį druskos ir cukrų. Išmaišykite gerai, kad neliktų cukraus granulių.&lt;/p&gt;&lt;p&gt;Sudėkite sodą, sumaišytą su citrinos sultimis.&lt;/p&gt;&lt;p&gt;Įmaišykite miltus.&lt;/p&gt;&lt;p&gt;Tešlą maišykite tol, kol neliks gumulėlių.&lt;/p&gt;&lt;p&gt;Kepkite blynus įkaitintoje keptuvėje.&lt;/p&gt;&lt;p&gt;Patepkite keptuvę aliejumi arba sviestu.&lt;/p&gt;&lt;p&gt;Kepkite, kol abi blyno pusės bus gražiai pageltę.&lt;/p&gt;&lt;p&gt;Tiesa, blynus apverskite tik vieną kartą, kad nesubliukštų. :)&lt;/p&gt;', '2773.jpg'),
(119, 'Omletas su kumpiu ir žirneliais', '&lt;p&gt;Sukapokite svogūną, česnaką ir kubeliais supjaustykite kumpį.&lt;/p&gt;&lt;p&gt;Šio ispaniško kumpio yra pirkti visuose didžiuosiuose prekybos centruose, bent jau IKI tikrai.:) Pakepinkite viską iki 5 minučių.&lt;/p&gt;&lt;p&gt;Suberkite žirnelius.&lt;/p&gt;&lt;p&gt;Išplakite kiaušinius su prieskoniais ir supilkite į keptuvę.&lt;/p&gt;&lt;p&gt;Kepkite apie 10 minučių ant vidutinio kaitrumo ugnies. Apkepkite iš  abiejų pusių.&lt;/p&gt;', '2909.jpg'),
(120, 'Netradiciniai šaltibarščiai su aguročiu', '&lt;p&gt;Į gilų dubenį supilame kefyrą ir pieną. Virtus kiaušinius sukapojame, aguročius supjaustome. Dedame viską į dubenį, išmaišome, pagardiname druska ir smulkintais krapais Tiekiam su šiltomis bulvėmis.&lt;/p&gt;', '5460.jpg'),
(121, 'Degtinės kokteilis', '&lt;p&gt;Į degtinę įmaišykite rudąjį cukrų.&lt;/p&gt;&lt;p&gt;Pridėkite ledukų, užpilkite cukraus pudra, įmeskite citrinos skiltelių.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '2019.jpg'),
(122, 'Bobutės duonos alus', '&lt;p&gt;Suberiame duona į indą ir užpilame 15 litrų verdančio vandens.&lt;/p&gt;&lt;p&gt;Šitą visą gėrį palaikome apie porą valandų, kad pritrauktų kvapus ir spalvą.&lt;/p&gt;&lt;p&gt;Kol aušta duona su 2 litrais vandens verdame apynius. Aš juos verdu apie valandą, kad pasidarytų rudas kartus skystimas. Po 2 val. perkošiu duoną, supilu perkoštą apynių viralą, supilu cukrų ir ataušinu iki kokių 25 laipsnių. Mieles sutriname su cukrumi , palaukiame truputį, kol pradės kilti, tada mieles su visu stiklainiu įleidžiame į puodą ir paskandiname. Puodą uždengiame plėvele, dangčiu ir apklojame užklotais. Paliekame visą tai šiltai 24 val. Po paros ragaujame, jei viskas gerai, pilame į bačkutę arba butelaičius ir padedame šaltai. Skanaus&lt;/p&gt;', '1038.jpg'),
(123, 'Blyneliai „Peliukas Mikis“', '&lt;p&gt;Kiaušinius išplakame su pienu, cukrumi, druska ir ištirpintu sviestu. Suberiame miltus ir gerai išmaišome tešlą. Kepame blynus. Į keptuvę aliejaus pilti nereikia, nes į tešlą dėjome sviesto. Jei keptuvė gera, nesubraižytu paviršiumi, blynai tikrai nelips. :)&lt;/p&gt;&lt;p&gt;Vienus blynus kepkite mažesnius, bus ausytės, o kitus didesnius, nes bus galva. Sudėliokite iš blynelių meškučio galvą. Akytes ir burną nupieškite su uogiene, ausis taip pat paryškinkite su uogiene. :)&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '2366.jpg'),
(124, 'Juoda arbata su citrina ir mėta', '&lt;p&gt;Mėtos, citrinų žievelės ir arbatžolės užplikomos vandeniu.&lt;/p&gt;&lt;p&gt;Nukošiama.&lt;/p&gt;&lt;p&gt;Arbatą paskaniname cukrumi.&lt;/p&gt;', '1731.jpg'),
(125, 'Antienos filė su traškučiais ir ožkos pieno sūriu', '&lt;p&gt;Antienos odelę įpjaukite  rombais, apibarstykite aitriosiomis paprikomis ir palaukite, kad skonis būtų geresnis. Apskrudinkite iš abiejų pusių ir kepkite apie 8 minutes iki 200 °C temperatūros  įkaitintoje orkaitėje.&lt;/p&gt;&lt;p&gt;Ištraukę apie 5 minutes palaukite, tada supjaustykite griežinėliais. Sumaišykite visus čatnio produktus, kad masė būtų vientisa ir puri.&lt;/p&gt;&lt;p&gt;Iš keptuvės nuliekite riebalų perteklių ir supilkite į keptuvę vyšnių čatnį. Pavirinkite 5–10 minučių, kol čatnis sutirštės.&lt;/p&gt;&lt;p&gt;Apie 5 minutes skrudinkite kukurūzų traškučius iki 180 °C įkaitintoje orkaitėje. Patiekdami ant traškučių sudėkite antienos filė, čatnį ir sūrį. Papuoškite bazilikų lapais ir juodaisiais pipirais.&lt;/p&gt;', '6506.jpg'),
(126, 'Makaronai su avokadų padažu', '&lt;p&gt;Makaronus verdame apie 20 minučių.&lt;/p&gt;&lt;p&gt;Išvirusius nukošiame ir įmaišome 2 šaukštus aliejaus, kad nesuliptų ir būtų ne tokie sausi.&lt;/p&gt;&lt;p&gt;Avokadą ir citrinos griežinėlius sutriname iki tyrelės konsistencijos.&lt;/p&gt;&lt;p&gt;Į avokado masę įmaišome aliejų, sūrį ir prieskonius.&lt;/p&gt;&lt;p&gt;Makaronus patiekiame su arugula ir padažu. :)&lt;/p&gt;', '3164.jpg'),
(127, 'Šventiniai sūrio vėrinukai', '&lt;p&gt;Puikaus skonio ir nuostabios išvaizdos užkandėlė kai reikia greit, skaniai ir estetiškai paruošti stalą.&lt;/p&gt;&lt;p&gt;Ant dantų krapštukų verkite kubelį pelėsinio sūrio, griežinėlį alyvuogės ir agurko, verkite po vyšninį pomidorą.&lt;/p&gt;&lt;p&gt;Dantų krapštuku perverkite vieną galą agurko griežinėlio, tada verkite mažą gabaliuką paprikos, alyvuogę ir perverkite vėl agurką, kad gautųsi burė. Ant vieno pagaliuko galo užverkite kubelį fermentinio sūrio, ant kito - gabalėlį paprikos.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '6488.jpg'),
(128, 'Duona su svogūnais ir sūriu', '&lt;p&gt;Paruoškite tešlą:&lt;/p&gt;&lt;p&gt;užpildykite sausas mieles šiltu vandeniu ir išmaišykite.&lt;/p&gt;&lt;p&gt;Kartu supilti šiek tiek miltų ir druskos.&lt;/p&gt;&lt;p&gt;Viską išmaišome ir paliekame 10 minučių.&lt;/p&gt;&lt;p&gt;Galiausiai suberiame likusius miltus ir išmaišome.&lt;/p&gt;&lt;p&gt;Svogūnus supjaustome norimo dydžio kubeliais ar griežinėliais.&lt;/p&gt;&lt;p&gt;Ir šiek tiek pakepiname ant alyvuogių aliejaus ar sviesto.&lt;/p&gt;&lt;p&gt;Pakepintus svogūnus ir sutarkuotą sūrį įmaišome į tešlą.&lt;/p&gt;&lt;p&gt;Tešlą labai gerai išminkome.&lt;/p&gt;&lt;p&gt;Kai tešlą išminkome, suformuojame kepalo formą, ištepame alyvuogių aliejumi ir paliekame 1-2 val., kad pakiltų.&lt;/p&gt;&lt;p&gt;Galiausiai duoną kepame orkaitėje, įkaitintoje iki 200 laipsnių.&lt;/p&gt;&lt;p&gt;Duoną kepame 25-30 min. Patikrinti ar iškepė duona, galima su mediniu pagaliuku.&lt;/p&gt;&lt;p&gt;Gero apetito!&lt;/p&gt;', '1384.jpg'),
(129, 'Žuvies pyragas', '&lt;p&gt;Grietinę išmaišome su ištirpintu sviestu ir druska. Įmaišome miltus ir kepimo miltelius. Gerai išminkome tešlą.\n                                                                Tešla išklojame kepimo formą: ir dugną, ir šonus.\n                                                                Konservuotą žuvį (savo sultyse ar aliejuje) išmaišome su susmulkintais žalumynais. Sudedame ant tešlos. Išplakame kiaušinius su grietine ir miltais. Supilame ant žuvies.\n                                                                Kepame 40 - 45 minutes 180 °C orkaitėje.&lt;/p&gt;', '3409.jpg'),
(130, 'Naminių makaronų suktinukai su mėsa', '&lt;p&gt;Miltus išsijojame su druska. Įpilame aliejaus, verdančio vandens ir išmaišome. Kai tešla pravėsta, įmušame kiaušinį (nebūtina) ir minkome tol, kol pasidaro vientisa masė.&lt;/p&gt;&lt;p&gt;Tuomet iškočiojame tešlą, bei padaliname į du lakštus.&lt;/p&gt;&lt;p&gt;Faršą išmaišome su prieskoniais ir 1 smulkintu svogūnu. Sudedame ant lakštų. Tešlą su faršu susukame į ritinėlius.&lt;/p&gt;&lt;p&gt;Juos sudedame į aliejumi pateptą keptuvę. Supilame susmulkintus pomidorus, svogūną, kečupą. Šiek tiek įpilame vandens arba sultinio, kad nesviltų.&lt;/p&gt;&lt;p&gt;Troškiname apie 20-30 min. ant vidutinio kaitrumo ugnies.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '1646.jpg'),
(131, 'Avižinė košė', '&lt;p&gt;Į verdantį vandenį suberti avižines kruopas. Ant lengvos ugnies vis pamaišant virti, kol kruopos suminkštės. Po to, lėtai maišant, sudėti smulkiai sutarkuotą bulvę (bet nebūtina), pavirinti, suberti cukrų, druską ir nukaitus leisti pastovėti kelias minutes, kad košė subrinktų.&lt;/p&gt;&lt;p&gt;Prie avižinės košės paduoti sviestą, saldų pieną.&lt;/p&gt;', '4097.jpg'),
(132, 'Desertinė grikių košė', '&lt;p&gt;Į verdantį vandenį sudėti virti grikius. Dėti cinamono, vanilės ekstrakto, druskos. Baigiant virti supilti grietinėlę ir klevų sirupą. Patiekti su šviežiais arba šaldytais vaisiais ir riešutais.&lt;/p&gt;', '4110.jpg'),
(133, 'Šalta arbata su citrina', '&lt;p&gt;Ataušintą, pasaldintą arbatą supilame į stiklines, į kurias jau įdėta ledukų ir po griežinėlį citrinos. Geriama pro šiaudelį.&lt;/p&gt;', '1834.jpg'),
(134, 'Putojančio vyno kokteilis', '&lt;p&gt;Likerį išplakti su džinu ir sultimis.&lt;/p&gt;&lt;p&gt;Į taures lėtai pilti kartu ir sultis, ir putojantį vyną.&lt;/p&gt;&lt;p&gt;Papuošti braškėmis.&lt;/p&gt;', '1667.jpg'),
(135, 'MARTINI PINK ORANGE', '&lt;p&gt;Sumaišomas Martini Rosato ir apelsinų sultys.&lt;/p&gt;&lt;p&gt;Patiekiama aukštoje taurėje su ledukais ir apelsino skiltele.&lt;/p&gt;', '3388.jpg'),
(136, 'Karštas vynas nuo kosulio', '&lt;p&gt;Užkaitinti vyną, įpilti citrinos sulčių (ar tiesiog įmest porą griežinėlių), supilti konjaką.&lt;/p&gt;&lt;p&gt;Gerti labai mažais gurkšneliais, pageidautina jau gulint lovoje.&lt;/p&gt;&lt;p&gt;Šį receptą sužinojau ir patyrusio gydytojo, homeopato. Padeda!&lt;/p&gt;', '1132.jpg'),
(137, 'Troškintas daržovių garnyras', '&lt;p&gt;Visas daržoves supjaustome norimo dydžio gabaliukais ir troškiname svieste apie 10 min. Po to supilame grietinėlę, įberiame muskato, baltųjų pipirų, ir čili pipiro miltelių ir dar kelias minutes patroškiname.&lt;/p&gt;', '4275.jpg'),
(138, 'Karšti sumuštiniai su krevetėmis', '&lt;p&gt;Baltos duonos riekes ištepame plonai sviestu. Smulkiai supjaustome kiaušinius, krevetes ir sumaišome su tarkuotu sūriu bei majonezu. Gautą masę pagardiname juodaisiais pipirais, druska, krapais, bei tepame ant duonos ir dedame 5 minutėms į įkaitintą orkaitę.&lt;/p&gt;', '5958.jpg'),
(139, 'Airiška kava', '&lt;p&gt;Griežinėliu citrinos ištepkite taurės vidų. Supilkite kavą ir viskį, dėkite cukraus, plaktos grietinėlės ir užbarstykite tarkuoto šokolado.&lt;/p&gt;&lt;p&gt;Į sveikatą ;)&lt;/p&gt;', '4292.jpg'),
(140, 'Perlinių kruopų košė su spirgučiais', '&lt;p&gt;Perlines kruopas keletą valandų mirkyti šaltame vandenyje, po tu nusunkti, užplikyti nedideliu kiekiu verdančio vandens ir virti, kol suminkštės.\n                                                                Lašinukus supjaustyti kubeliais ir pakepinti. Sumaišyti su išvirusia koše, pabarstyti kapotais žalumynais ir pagardinti druska.&lt;/p&gt;', '4198.jpg'),
(141, 'Jogurto ir agurkų padažiukas', '&lt;p&gt;Agurką (jei ilgas, tai apie 7 cm) nulupkite ir sutarkuokite burokine tarka.&lt;/p&gt;&lt;p&gt;Sutrinkite česnako skiltelę.&lt;/p&gt;&lt;p&gt;Jogurtą sumaišykite su aliejumi, druska ir česnaku.&lt;/p&gt;&lt;p&gt;Į jogurtą įmaišykite agurką.&lt;/p&gt;&lt;p&gt;Labai skanus padažas, tinkamas prie sūrių sausainiukų ar mėsos. :)&lt;/p&gt;', '2784.jpg'),
(142, 'Braškinis kokteilis su romu', '&lt;p&gt;Visus komponentus sumaišyti, supilti į atšaldytą taurę ir papuošti uogomis.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '3917.jpg'),
(143, 'Brendžio ir konjako kokteilis "Vyšnių simfonija"', '&lt;p&gt;Visus komponentus kartu su ledu dėkite į kokteilių maišytuvą, gerai suplakite ir mėgaukitės!&lt;/p&gt;', '3940.jpg'),
(144, 'Pieno ir graikinių riešutų gėrimas', '&lt;p&gt;Riešutus sukapoti.&lt;/p&gt;&lt;p&gt;Į pieną įmaišyti cukrų ir riešutus.&lt;/p&gt;&lt;p&gt;Viską kaitinti ant ugnies apie 15 min.&lt;/p&gt;', '1736.jpg'),
(145, 'Kisielius su džiovintais vaisiais', '&lt;p&gt;Džiovintus vaisius nuplauti ir užpilti karštu vandeniu. Palikti valandai brinkti.\n                                                                Pagal instrukciją išvirti dvi  pakuotes kisieliaus. Tada suberti džiovintus vaisius ir ataušinti. Į ataušusį kisielių berti smulkintus riešutus.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '4036.jpg'),
(146, 'Lašišos vieno kąsnio sumuštinukai', '&lt;p&gt;Iš forminės duonos padarome tulpės formos gėlytes ir skrudiname orkaitėje 200° C temperatūroje 2-3 min. Lašišą sukapojame smulkiai ir formuojame rutuliukus. Tada imame skrudintą duonelę, dedame ant jos lašišos kamuoliuką. Ant viršaus per konditerinį maišelį iš minkšto sviesto padarome rožytę. Persmeigiame smeigtuku ir papuošiame petražolės šakele.&lt;/p&gt;&lt;p&gt;Skanu ir labai puikiai tinka vakarėliui.&lt;/p&gt;', '2927.jpg'),
(147, 'Šokoladiniai lietiniai blynai', '&lt;p&gt;Ištirpiname sviestą ir šokoladą.&lt;/p&gt;&lt;p&gt;Kai šiek tiek pravėsta įmaišome kakavą, romą.&lt;/p&gt;&lt;p&gt;Kiaušinius išplakame su druska ir cukraus pudra.&lt;/p&gt;&lt;p&gt;Šokolado masę sumaišome su kiaušinių plakiniu.&lt;/p&gt;&lt;p&gt;Įmaišome pieną, o po to miltus.&lt;/p&gt;&lt;p&gt;Įkaitiname keptuvę (nesvylančiu paviršiumi). Kepame plonus blynus per visą keptuvės plotį.&lt;/p&gt;', '3467.jpg'),
(148, 'Bulviniai blynai su dešrelėmis', '&lt;p&gt;Bulves nulupti, sutarkuoti burokine tarka, truputį nuspausti sultis. Įmušti kiaušinį, sudėti miltus, druską, pipirus ir griežinėliais supjaustytas dešreles. Viską išmaišyti ir kepti ant aliejaus. Kepti kiekvieną blyno pusę po 3-4 minutes. Patiekti su grietine. Skanaus.&lt;/p&gt;', '3271.jpg'),
(149, 'Šaltalankio sultys', '&lt;p&gt;Šviežias uogas nuplauti, susmulkinti, supilti į emaliuotą indą, užpilti šiltu vandeniu (0,2 litro 1 kg uogų), pašildyti ir nuspausti. Gautas sultis nukošti, pašildyti iki virimo, išpilstyti į stiklainius (0,5 L), sterilizuoti 10 minučių, hermetiškai uždaryti ir ataušinti.&lt;/p&gt;', '5207.jpg'),
(150, 'Santa Maria: Virtos bulvės su sviestu', '&lt;p&gt;Virkite bulves, sumaišytas su prieskonių mišiniu, kol suminkštės. Tada nusausinkite ir perpjaukite per pusę.&lt;/p&gt;&lt;p&gt;Ant viršaus uždėkite sviesto ir krapų. Patiekite dar šiltas.&lt;/p&gt;&lt;p&gt;Patarimas! Iki Naujųjų metų bulves galite valgyti su lupenomis, vėliau patariame jas naudoti nuluptas.&lt;/p&gt;', '3615.jpg'),
(151, 'Sluoksniuotas traškus tešlainių desertas su "POÉMA Creme" (pagal Darių Dabrovolską)', '&lt;p&gt;Tešla: suplakame kiaušinius su grietine ir druska, dedame miltus ir minkome tešlą kol galime kočioti. Plonai iškočiojame, supjaustome norima forma ir subadome šakute. Gerai įkaitiname aliejų ir verdame tešlainius, kol gražiai pagelsta. Tuomet nusausiname riebalų perteklių.&lt;/p&gt;&lt;p&gt;Kremas: plakame grietinėlę (250 ml) su cukrumi, vėliau įdedame vanilės lazdelės sėklas , išplakę sumaišome su mascarpone sūriu.&lt;/p&gt;&lt;p&gt;Padažo ingredientus (likerį, juodąjį šokoladą ir 70 ml grietinėlės) sudedame į puodą ir kaitiname, kol ištirpsta šokoladas ir gaunasi vientisa masė.&lt;/p&gt;&lt;p&gt;Viliojančio saldumo!&lt;/p&gt;', '3247.jpg'),
(152, 'Konservuotos paprikos', '&lt;p&gt;Paprikas išvalyti nuo sėklų ir supjaustyti vidutinio dydžio juostelėmis. Česnaką, krapus ir petražoles smulkiai supjaustyti. Į verdantį marinatą dėti česnaką ir virti 5 minutes. Po 5 minučių dėti paprikas ir žalumynus ir viską virti 15-20 minučių. Sudėti į stiklainius ir užsukti.&lt;/p&gt;', '3402.jpg'),
(153, 'Sumuštinukai "Sweat dreams"', '&lt;p&gt;Išskleiskite lavašą, jį ištepkite negailėdami sūreliu. Geriausia šį patiekalą gaminti dviese. Rekomenduojama poroms norint praleisti gerai vakarą. Taigi ištepę sūriu lavašą apdedame krabų mėsa, agurkėliais. Kitą lavašą apdėkite kumpiu ir agurkais.  Lavašo lapus susukame. Tada su peiliu supjaustome norimo dydžio gabaliukais.&lt;/p&gt;', '3627.jpg'),
(154, 'Skanusis abrikosų džemas', '&lt;p&gt;Abrikosus nuplaukite,  nusausinkite, perpjaukite pusiau ir pašalinkite kauliukus.\n                                                                Tada abrikosus apipilkite cukrumi ir virkite  ant vidutinės ugnies. Po užvirimo virkite apie 15 minučių ir nuimkite nuo ugnies. Leiskite atvėsti ir procedūrą pakartokite kelis kartus.\n                                                                Tada virinį supilstykite į sterilius stiklainius, sandariai uždarykite ir apvožkite.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '3843.jpg'),
(155, 'Alaus kokteilis', '&lt;p&gt;Ledukus sudėti į stiklinę.&lt;/p&gt;&lt;p&gt;Suberti imbiero miltelius ir supilti citrinos sultis.&lt;/p&gt;&lt;p&gt;Viską užpilti alumi.&lt;/p&gt;', '2230.jpg'),
(156, 'Krevečių salotos su padažu', '&lt;p&gt;Padažas:&lt;/p&gt;&lt;p&gt;citrinų sultis su maišyti su aliejumi. Įmaišyti kmynus, sugrūstą česnako skiltelę, garstyčias, druską ir pipirus.&lt;/p&gt;&lt;p&gt;Salotos:&lt;/p&gt;&lt;p&gt;Marinuotas arba virtas krevetes sumaišyti su avokadų ir pomidorų skiltelėmis bei kalendra (nebūtina).&lt;/p&gt;&lt;p&gt;Salotas patiekti su padažu.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '2152.jpg'),
(157, 'Kokteilis „Raudona tekila“', '&lt;p&gt;Visas sudėtines dalis atšaldome. Sumaišome tekilą ir likerį.&lt;/p&gt;&lt;p&gt;Į taurę supilame sultis, tekilą su likeriu ir uždedame ledukų.&lt;/p&gt;', '1662.jpg'),
(158, 'Sluoksniuotos tešlos sumuštiniai', '&lt;p&gt;Atšildykite tešlą ir lakštus padėkite ant miltais pabarstyto stalo. Tada tešlą aptepkite riebalais ir garstyčiomis.\n                                                                Sutarkuokite sūrį, supjaustykite pomidorą ir agurkus. Ant tešlos sluoksniuokite įdarą: dėkite tarkuotą sūrį ir pabarstykite pipirais ir prieskoninėmis žolėmis. Tada sudėkite pjaustytą kumpį (galima apkepti keptuvėje) kartu su pomidorais ir agurkais. Atsargiai suvyniokite tešlą (suktinio daryti nereikia, svarbiausia, kad įdaras neištekėtų pro šonus).\n                                                                Kepinius aptepkite kiaušinio plakiniu ir pabarstykite pipirais. Kepkite orkaitėje  15 - 20 minučių.&lt;/p&gt;', '5973.jpg'),
(159, 'Viktorijos bulvių apkepas', '&lt;p&gt;Bulves nulupti, supjaustyti plonais griežineliais. Česnaką nulupti, smulkiai sukapoti, tada sumaišyti su druska, pipirais, muskatu ir kapotais žalumynais.&lt;/p&gt;&lt;p&gt;Sūrį sutarkuoti stambia tarka. Dviem šaukštais sviesto ištepti kepimo skardą. Sluoksniais dėti bulvių griežinėlius, ant viršaus berti truputį česnako ir žalumynų mišinio, tada tarkuoti sūrio ir vėl dėti bulves. Taip sluoksniuoti, kol baigsis visi produktai.&lt;/p&gt;&lt;p&gt;Grietinėlę ir pieną išplakti ir užpilti ant bulvių apkepo. Tada suberti likusį sūrį ir kepimo indą uždengti folija. Kepti įkaitintoje orkaitėje apie 45 minutes.&lt;/p&gt;&lt;p&gt;Patiekti su žalumynais ir mėgstamu padažu ar grietine.&lt;/p&gt;', '3953.jpg'),
(160, 'Kumpio mišrainė', '&lt;p&gt;Išvirtus kiaušinius supjaustykite kubeliais, susmulkinkite abiejų rūšių kumpius ir išmaišykite su majonezu.&lt;/p&gt;', '6490.jpg'),
(161, 'Pomidorų ir čili padažas', '&lt;p&gt;Padažas yra aštrus, tad kas nemėgstate aštriai čili pipirų galite dėti mažiau.&lt;/p&gt;&lt;p&gt;Pomidorus užpilti verdančiu vandeniu ir nulupti odeles. Pomidorus be odelių ir čili pipirus supjaustyti kubeliais.&lt;/p&gt;&lt;p&gt;Česnaką sutrinti arba supjaustyti labai smulkiai.&lt;/p&gt;&lt;p&gt;Pomidorus išmaišyti su čili pipirais, imbieru, česnaku, cukrumi, actu ir vynu.&lt;/p&gt;&lt;p&gt;Pakaitinti padažą ant ugnies (vis pamaišant) apie 15-20 min.&lt;/p&gt;&lt;p&gt;Laikyti stiklainyje.&lt;/p&gt;&lt;p&gt;Padažas išsilaiko gana ilgai. Jei laikysite šaldytuve, naudoti galėsite iki 2 mėnesių.&lt;/p&gt;', '1898.jpg'),
(162, 'Svarainių kompotas', '&lt;p&gt;Kompotui tinka tik visiškai prinokę vaisiai. Pirmiausia jie nuplaunami, pašalinami koteliai, supjaustomi skiltelėmis, sumetami į karštą 85° C vandenį ir jame išlaikomi 30 min., kad suminkštėtų. Po to vaisiai išgriebiami į šaltą vandenį, kad atšaltų, sudedami į stiklainius ir užpilami saldžiu karštu sirupu (1 L vandens —500 g cukraus). Stiklainiai pridengiami dangteliais ir kaitinami 85° C temperatūroje 15 min. kitame puode. Iškaitinti stiklainiai nedelsiant hermetiškai uždaromi.&lt;/p&gt;', '4043.jpg'),
(163, 'Kreminė krevečių sriuba', '&lt;p&gt;Užvirinti sultinį ir sudėti krevetes.\n                                                                Keptuvėje, ant mažos ugnies, ištirpinti sviestą ir maišant suberti miltus.\n                                                                Gautą padažą supilti į sriubą,  supilti grietinėlę ir virti 15 minučių. Pagardinti druska ir prieskoniais.  Patiekti šiltą.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '5451.jpg'),
(164, 'Šokoladinis tortukas', '&lt;p&gt;Šokolado gabaliukus, sviestą ir 2 šaukštus vandens sudėti į indą ir kaitinti vonelėje virš garų, kol šokoladas ir sviestas ištirps. Atvėsinti, supilti cukrų su kiaušiniais ir išsukti. Miltus, sumaišytus su kepimo milteliais, taip pat supilti i masę. Išmaišius  dėti i dvi apvalias formas. Kepti 160 laipsnių temperatūroje apie 25-30 min. Atvėsinus padalinti į 4 dalis.&lt;/p&gt;&lt;p&gt;Kremui: kondensuotą pieną ir sviestą išsukti iki vientisos masės ir pertepti lakštus.&lt;/p&gt;&lt;p&gt;Glajui: ant silpnos ugnies užvirinti ingredientus ir aplieti tortą. Dėti šaltai, kad sustingtų. Puošti citrinos sulčių ir cukraus pudros glajumi.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '3629.jpg'),
(165, 'Ryžių kepsneliai', '&lt;p&gt;Sutarkuotus ar pjaustytus kopūstus dėti virti į nedidelį kiekį vandens ir virti 10- 15 minučių.\n                                                                Pridėti pomidorų pastos, smulkiai pjaustytų svogūnų. Virti, kol išgaruoja skystis.\n                                                                Prie virtų daržovių pridėti 6 šaukštus ryžių, įmušti kiaušinį ir išminkyti masę.\n                                                                Į vieną ranką paimti džiūvėsėlių (kad masė neliptų) ir šaukšto pagalba formuoti kepsniuką. Dėti į įkaitintą aliejų ir kepti 2- 3 minutes iš abiejų pusių.&lt;/p&gt;', '4520.jpg'),
(166, 'Šventiniai sumuštiniai', '&lt;p&gt;Kiekvieną batono riekę aptepkite majonezo ir česnakų mišiniu, tada riekes pabarstykite smulkintais svogūnų laiškais ir ant kiekvienos riekės uždėkite po šprotą bei pabarstykite sūriu.\n                                                                Sumuštinius kepkite 200 °C orkaitėje apie 15 minučių.\n                                                                Patiekite su žalumynais ir alyvuogėmis.&lt;/p&gt;', '5473.jpg'),
(167, 'Makaronų apkepas su varške', '&lt;p&gt;Varškę pertriname, sudedame kiaušinius, razinas, cukrų, vanilinį cukrų ir gerai išsukame. Makaronus išverdame ir sudedame į skardelę, pateptą aliejumi. Ant viršaus dedame varškės masę, uždedame sviesto gabaliukus ir kepame 180°C orkaitėje apie 30-35 minutes.&lt;/p&gt;', '372.jpg'),
(168, 'Greita užkandėlė šventiniam stalui', '&lt;p&gt;Greipfrutą nuplaukite, perpjaukite pusiau ir padėkite į lėkštę. Kumpį, sūrį, papriką supjaustykite gražiais gabaliukais, verkite ant dantų krapštuko ir vėrinukus susmaigstykite į vaisių.&lt;/p&gt;&lt;p&gt;Subadyto greipfruto aromatas puikiai padeda atsiskleisti užkandžių aromatui, tad nebijokite eksperimentuoti, naudoti įvairius sūrius, skirtingų spalvų alyvuoges, paprikas.&lt;/p&gt;', '6465.jpg'),
(169, 'Blyneliai su vaisiais ir šokoladu', '&lt;p&gt;Įdarui:&lt;/p&gt;&lt;p&gt;3 bananų&lt;/p&gt;&lt;p&gt;3 obuolių&lt;/p&gt;&lt;p&gt;Šokolado ( pagal skonį)&lt;/p&gt;&lt;p&gt;75 g sviesto&lt;/p&gt;&lt;p&gt;3 arbatinius šaukštus cukraus&lt;/p&gt;&lt;p&gt;Užpilui:&lt;/p&gt;&lt;p&gt;100 g kakavos&lt;/p&gt;&lt;p&gt;200 ml pieno&lt;/p&gt;&lt;p&gt;200 g cukraus&lt;/p&gt;&lt;p&gt;Įdaras:&lt;/p&gt;&lt;p&gt;Bananą ir obuolį nulupame ir supjaustome kubeliais. Ant silpnos ugnies ištirpiname šokoladą su sviestu ir sumaišome su cukrumi.Kartu su sviestu, šokoladu ir cukrumi sumaišome supjaustytus bananus ir obuolius.Šiek tiek pakaitiname.Nuimame puodą ir atvėsus padedame į šaldytuvą, kol gaminsime blynus.&lt;/p&gt;&lt;p&gt;Tešla:&lt;/p&gt;&lt;p&gt;Tešlai skirtus produktus sudėti į dubenį ir išplakti maišytuvu.  Kepti per visą keptuvę ant mažos ugnies ir versti tada, kai blynas nebekibs prie keptuvės (jei įpylėte reikiamą kiekį aliejaus) ir bus apskrudęs, bet neperdegęs.&lt;/p&gt;&lt;p&gt;Užpilas:&lt;/p&gt;&lt;p&gt;Įpusėjus kepti lietinius pradėti virti užpilą. Į dubenį įpilti pieną ir laukti kol užkais, bet neužvirs. Tada sudėti kakavą ir cukrų ir visą laiką maišyti, kad nesusidarytu gumulėlių. Virti tol, kol sutirštės.&lt;/p&gt;&lt;p&gt;Tada ant blyno sudėti įdarą , suvynioti. Užpilti šokoladą ir papuošti. Labai tinka vaniliniai ledai.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '1564.jpg'),
(170, 'Moliūgų sriuba „Tau, mano meile“', '&lt;p&gt;Moliūgą nulupti, supjaustyti riekėmis, svogūną nulupti ir ketvirčiuoti, česnako skilteles nulupti, bet nepjaustyti. Tada moliūgą, svogūną, česnako skilteles, salierą sudėti į skardą, užpilti keletu šaukštu aliejaus, pabarstyt druska ir pipirais, čiobreliais, viską rankomis sumaišyti ir dėti į 180-190° C orkaitę, kepti apie valandą, kol suminkštės.&lt;/p&gt;&lt;p&gt;Tada jau visą iškepusį ir suminkštėjusį moliūgą, kartu su svogūnais ir salierais, ir česnakais, dėti į puodą, ir užpilti vištienos sultiniu tiek, kad apsemtų. Pavirti dešimt minučių, išimti salierą ir čiobrelio stagarus, tada belieka tik sriubą sutrinti su trintuvu ir įpilti pieno arba grietinėlės. Užvirti ir viskas, galima valgyti. :)&lt;/p&gt;', '3054.jpg'),
(171, 'Kiaušinukai Kūčioms', '&lt;p&gt;Išverdame kiaušinius, atšaldome, išimame trynius ir sudedame į dubenį. Svogūną susmulkiname ir apkepiname svieste. Atvėsiname.\n                                                                Vištieną supjaustome gabaliukais, apkepiname svieste ir atšaldome.\n                                                                Kiaušinių trynius, svogūną ir vištieną dedame į vieną dubenį ir sumaišome su tarkuotu sūriu, smulkintais krapais, barstome druskos, pipirų ir išmaišome. Mase įdarome kiaušinių baltymus.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '5610.jpg'),
(172, 'Moliūgų uogienė su vaisiais', '&lt;p&gt;Vaisius gerai nuplauti ir nusausinti. Moliūgų žievę nulupti ir pašalinti sėklas.\n                                                                Supjaustyti kubeliais apelsinus, citrinas (neluptus), moliūgą, tada dėti į puodą, pilti cukraus, gerai išmaišyti ir virti ant mažos ugnies 30 minučių. Palikti 4- 6 valandoms visiškai atvėsti.\n                                                                Pilstyti į sterilizuotus stiklainius, sandariai uždaryti ir apvožti. Taip palaikyti per naktį.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '3841.jpg'),
(173, 'Vaflių tortas su šokoladu', '&lt;p&gt;Kambario temperatūros sviestą sumaišyti su kondensuotu pienu. Masę pertepti vaflių lakštus, apibarstyti šokolado drožlėmis ir tortą padėti 3 valandoms į šaldytuvą stingti.&lt;/p&gt;&lt;p&gt;Skanaus  ir labai paprasta :)&lt;/p&gt;', '4068.jpg'),
(174, 'Trintos mėlynės ir žemuogės žiemai', '&lt;p&gt;Uogas perrenkame, nuplauname ir perliejame šaltu virintu vandeniu. Suberiame į tinklelį ir pertriname mediniu šaukštu.&lt;/p&gt;&lt;p&gt;Į pertrintas uogas suberiame cukrų ir maišome, kol šis ištirpsta.&lt;/p&gt;&lt;p&gt;Taip paruoštas uogas supilame į iškaitintus stiklainius ir uždarome dezinfekuotais dangteliais.&lt;/p&gt;&lt;p&gt;Laikome šaltai, geriausia šaldytuvo apatinėse lentynose.&lt;/p&gt;', 'no-photo-280x180.jpg'),
(175, 'Ledai su braškėmis', '&lt;p&gt;Suplakame pasaldintą grietinėlę.&lt;/p&gt;&lt;p&gt;Sutriname braškes ir sumaišome su vynu bei cinamonu.&lt;/p&gt;&lt;p&gt;Į desertines taures sluoksniais dedame ledus, trintas braškes ir plaktą grietinėlę.&lt;/p&gt;&lt;p&gt;Puošiame plakta grietinėle, braškių gabalėliais ir mėtų lapeliais.&lt;/p&gt;', '4447.jpg'),
(176, 'Miglės skrebučiai su pamidorais', '&lt;p&gt;Pamidorus supjaustyti mažais kubeliais ir pagardinti druska ir pipirais. Batono riekeles paskrudinti. Uždėkite pamidorų ir skanaukite ! :)&lt;/p&gt;', '6287.jpg'),
(177, 'Virtiniai su mėsa', '&lt;p&gt;Visus tešlai skirtus komponentus sumaišykite ir iškočiokite tešlą. Stiklinės pagalba iš tešlos išspauskite apskritimus.\n                                                                Į dubenį sudėkite mėsą, smulkintus svogūnus, berkite druskos, pipirų ir išmaišykite masę.\n                                                                Į kiekvieną tešlos apskritimo vidurį dėkite šiek tiek mėsos masės, perlenkite ir užlipinkite kraštus. Virkite virtinius pasūdytame verdančiame vandenyje apie 20 minučių. Patiekite su kepintais lašinukais ir svogūnų laiškais ar grietine.&lt;/p&gt;', '6088.jpg'),
(178, 'Grikių desertiniai pusryčiai', '&lt;p&gt;Grikius užpilkite 4- 5 stiklinėmis vandens ir bent valandai palikite išbrinkti.\n                                                                Smulkia tarka sutarkuokite morką ir padėkite atskirai.\n                                                                Elektriniu maišytuvu suplakite visus kitus ingredientus.\n                                                                Prieš patiekiant sumaišyti grikius, morkas ir pieno mišinį.&lt;/p&gt;&lt;p&gt;Labai puikus patiekalas pusryčiams.&lt;/p&gt;', '4111.jpg'),
(179, 'Varškės padažas', '&lt;p&gt;Česnako skilteles nulupau ir sutryniau.&lt;/p&gt;&lt;p&gt;Krapus sukapojau.&lt;/p&gt;&lt;p&gt;Varškę pertryniau su česnaku, krapais ir prieskoniais.&lt;/p&gt;&lt;p&gt;Labai skanu su skrudinta duona.&lt;/p&gt;', '2229.jpg'),
(180, 'Grybų salotos', '&lt;p&gt;Salotų lapais išklokite lėkštes.&lt;/p&gt;&lt;p&gt;Plonai supjaustykite kumpį ir grybus. Sukapokite česnaką ir svogūną. Ištirpinkite keptuvėje sviestą ir pakepinkite svogūnus bei česnakus 2 minutes. Sudėkite grybus ir mėsą. Pakepinkite dar 2-3 minutes.&lt;/p&gt;&lt;p&gt;Grybus sudėkite į lėkštes ant salotų ir apibarstykite skrudintos duonos kubeliais.&lt;/p&gt;', '2178.jpg'),
(181, 'Sūrio ir krabų lazdelių sumuštiniai', '&lt;p&gt;Fermentinį sūrį supjaustome išigai plonomis riekelėmis.&lt;/p&gt;&lt;p&gt;Kepimo fromą išklojame kepimo popieriumi ir išdedame fermentiniu sūriu.&lt;/p&gt;&lt;p&gt;Kepame 200 laipsnių orkaitėje apie 5-8 minutes, kol sūris pradeda lydytis.&lt;/p&gt;&lt;p&gt;Gaunasi vientisas lapas.&lt;/p&gt;&lt;p&gt;Atvėsiname.&lt;/p&gt;&lt;p&gt;Atvėsusį sūrio lapą ištepame lydytu ir pelėsiniu sūriu. Apdedame smulkiai pjaustytais agurkais ir dedame krapų lazdeles. Susukame į ritinėlio formą.&lt;/p&gt;&lt;p&gt;Dedame į šaldytuvą 2 valandoms.&lt;/p&gt;&lt;p&gt;Po to supjaustome griežinėliais.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '3445.jpg'),
(182, 'Greitieji vištienos kepsniukai', '&lt;p&gt;Kiaušinius gerai išplakite elektriniu plaktuvu, supilkite pieną ir šluotele išplakite. Vištienos krūtinėlę supjaustykite griežinėliais, pabarstykite druska. Kepsnelius pamerkite į kiaušinių plakinį, apvoliokite miltuose, tada džiūvėsėliuose ir kepkite įkaitintuose riebaluose ir abiejų pusių, kol gražiai apskrus.&lt;/p&gt;', '6086.jpg'),
(183, 'Persikų kokteilis', '&lt;p&gt;Į vandenį suberkite cukrų ir maišykite tol, kol ištirpsta.&lt;/p&gt;&lt;p&gt;Persikus nulupkite ir supjaustykite. Sutrinkite trintuvu.&lt;/p&gt;&lt;p&gt;Į persikų tyrelę supilkite sirupą, cukraus vandenį, pieną ir ledus.&lt;/p&gt;&lt;p&gt;Plakite, kol paviršiuje atsiranda pieno putų.&lt;/p&gt;', '2187.jpg'),
(184, 'Tortas su varškės kremu', '&lt;p&gt;Kiaušinius išplakame su 3 šaukštais karšto vandens, 50 g vanilinio cukraus ir 150 g cukraus.&lt;/p&gt;&lt;p&gt;Miltus sumaišome su krakmolu, kepimo milteliais ir viską supilame į kiaušinių masę, bei išmaišome.&lt;/p&gt;&lt;p&gt;Paruoštą masę supilame į kepimo popieriumi išklotą kepimo indą ir kepame apie 30 min., 160-180 laipsnių temperatūroje.&lt;/p&gt;&lt;p&gt;Iškepusį biskvitą atvėsiname ir padaliname į dvi dalis.&lt;/p&gt;&lt;p&gt;Paruošiame varškės kremą. Iš apelsinų išspaudžiame sultis, sumaišome su likusiu cukrumi ir vaniliniu cukrumi.&lt;/p&gt;&lt;p&gt;Ištirpiname želatiną ir išsukame su šalta liesa varške. Viską sumaišome su išplakta grietinėle.&lt;/p&gt;&lt;p&gt;Dalį kremo užtepame ant apatinės biskvito dalies, uždengiame kitu papločiu ir aptepame likusiu varškės kremu (ir torto kraštus).&lt;/p&gt;&lt;p&gt;Tortą apibarstome tarkuotu šokoladu. Skanaus!&lt;/p&gt;', '1611.jpg'),
(185, 'Makaronų apkepas su pievagrybiais', '&lt;p&gt;Makaronus išvirti, pievagrybius nuplauti, supjaustyti kubeliais ir apvirti (iki 5 minučių pavirti).&lt;/p&gt;&lt;p&gt;Sūrį sutarkuoti.&lt;/p&gt;&lt;p&gt;Į skardą sudėti pusę išvirtų makaronų, patepti grietine, dėti apvirtus pievagrybius, ant viršaus – tarkuotą sūrį, vėl aptepti grietine ir sudėti likusius makaronus. Kepti 200° C orkaitėje 15 min.&lt;/p&gt;', '2833.jpg'),
(186, 'Kreminis brendžio kokteilis', '&lt;p&gt;Į taures dėkite smulkintą ledą, medų, pilti brendį, dėti grietinėlės ir sumaišyti.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '3920.jpg'),
(187, 'Skani grikių košė', '&lt;p&gt;Grikius nuplikome verdančiu vandeniu ir sumaišome su grietine, dar įdedame sviesto ir druskos. Varškę ištriname su kiaušinių tryniais. Tada pasiimame stiklinį puodą, jį ištepame riebalais ir dedame pusę košės, tada ant jos varškės masę,o ant viršaus likusią dalį košės.Puodą dedame į orkaitę ir kepame 25 minutes.&lt;/p&gt;', '3509.jpg'),
(188, 'Žalioji kiaušinienė', '&lt;p&gt;Nusunkti žirnelius. Keptuvėje ištirpinti sviestą, sudėti žirnelius ir pakepti kelias minutes. Išplaktus su druska kiaušinius užlieti ant viršaus ir baigti kepti kol kiaušiniai iškeps. SKANIŲ PUSRYČIŲ&lt;/p&gt;', '553.jpg'),
(189, 'Perlinės kruopos su grybais ir daržovėmis', '&lt;p&gt;Perlinės kruopos mirkomos šaltame vandenyje keletą valandų arba per nakt. Vanduo nusunkiamas ir nusausinamos kruopos, užpilama vandens 3- 4 cm sluoksniu ir verdama, kol kruopos suminkštės.\n                                                                Morkas ir svogūnus nuplauti, supjaustyti kubeliais ir apkepinti įkaitintame aliejuje.\n                                                                Apdorotus grybus apkepinti kartu su morkomis ir svogūnais.\n                                                                Viską sumaišyti su išvirusiomis perlinėmis kruopomis, barstyti druskos ir šviežiai maltų pipirų.&lt;/p&gt;&lt;p&gt;Skanaus ;)&lt;/p&gt;', '4189.jpg'),
(190, 'Žemės riešutų sviestas', '&lt;p&gt;Riešutus pakepinkite sausoje keptuvėje iki auksinės spalvos. Tada nuimkite nuo riešutų luobelę ir riešutus suberkite i į dubenį. Smulkintuvu smulkinkite riešutus, pridėkite medaus, aliejaus ir toliau maišykite smulkintuvu. Dėkite truputį virinto vandens (1 - 3 šaukštai) ir smulkintuvu viską sutrinkite iki vienalytės masės.  Sviestą laikykite stiklainyje, šaltoje vietoje.&lt;/p&gt;', '6123.jpg'),
(191, 'Trinta pomidorų sriuba su sūriu', '&lt;p&gt;Su blenderiu sutriname bulves, konservuotus pomidorus (kartu su sultimis), pakepintus svogūnus ir česnakus. Užviriname sultinį ir į jį supilame sutrintą masę. Užviriname. Supilame į lėkštę, užtarkuojame sūrio, paplėšome baziliko lapelių ir patiekiame su skrudinta duona. Skanaus.&lt;/p&gt;', '1575.jpg'),
(192, 'Paprikos įdarytos kalakutiena', '&lt;p&gt;Nupjausti paprikų kepurėles ir išimti sėklas.\n                                                                Svogūną, česnaką susmulkinti. Kopūstą sutarkuoti. Mėsą supjaustyti gabaliukais. Viską sudėti į dubenį, suberti ryžius, barstyti druskos ir išmaišyti. Mase įdaryti paprikas.\n                                                                Į didelį puodą pilti vandens (ant dugno), uždengti dangtį ir troškinti apie valandą.&lt;/p&gt;', '6269.jpg'),
(193, 'Grikiai termose', '&lt;p&gt;Atsargiai berti grikius į termosą – atskirti lukštus ar kitus pastebėtus nešvarumus.&lt;/p&gt;&lt;p&gt;Pagardinti prieskoniais, galima įdėti gabalėlį sviesto.&lt;/p&gt;&lt;p&gt;Užkaitinti vandenį ir užpilti grikius. Atsargiai išmaišyti medine mentele, paragauti ir gal dar pagardinti.&lt;/p&gt;&lt;p&gt;Termosą užsukti ir palikti. Po kelių valandų galima valgyti.&lt;/p&gt;&lt;p&gt;Teko skaityti, kad tai Tibeto vienuolių receptas.&lt;/p&gt;', '957.jpg');
INSERT INTO `recipes` (`id`, `name`, `describtion`, `image_name`) VALUES
(194, 'Bajoriška kiaušinienė su voveraitėmis', '&lt;p&gt;Grybus gerai nuvalyti, nuplauti, nusausinti. Didesnes voveraites perpjauti pusiau. Sudėti į keptuvę su įkaitintu  &quot;Obelių&quot; sviesto skonio aliejų  aliejumi ir kepinti, kol išdaruos vanduo. Tada sudėti susmulkintą  svogūną ir kepinti, kol viskas gražiais apskrus. Kiaušinius suplakti su druska bei pipirais ir kepti, vis pamaišant, kol kiaušiniai iškeps.&lt;/p&gt;&lt;p&gt;Sotūs pusryčiai iš pieno produktų būdingi lenkų bajorų virtuvei, kuri glaudžiai susijusi  su lietuviškąją, o grybai  būdavo dažnas produktas didikų virtuvėje. Tad kiaušinienę su voveraitėmis galima drąsiai vadinti bajoriškais pusryčiais.&lt;/p&gt;&lt;p&gt;Šaltinis: Žurnalas &quot;Geri patarimai&quot;&lt;/p&gt;', '6584.jpg'),
(195, 'Bandelės su cinamonu', '&lt;p&gt;Į šiltą pieną įmaišome mieles. Po to suberiame pusę miltų.\n                                                                Išminkome ir paliekame uždengę rankšluosčiu šiltoje vietoje, kad pakiltų (30 min.).\n                                                                Įminkome cukrų, minkštą sviestą, kiaušinius ir likusius miltus. Uždengiame rankšluosčiu ir paliekame šiltoje vietoje 1 valandai, kad tešla vėl pakiltų.\n                                                                Cukrų sumaišome su cinamonu.\n                                                                Tešlą padaliname į 2-3 dalis ir iškočiojame iki plonų blynų. Apibarstome cinamono ir cukraus mišinius ir susukame.\n                                                                Tešlos ritinukus supjaustome griežinėliais.  Sudedame bandeles į kepimo skardą ir paliekame 20 minučių.\n                                                                Įkaitiname orkaitę iki 180 laipsnių ir kepame apie 25 - 30 minučių.&lt;/p&gt;', '3431.jpg'),
(196, 'Vyšnių kisielius', '&lt;p&gt;Vyšnias pertrinti, nuplauti šaltu vandeniu, išimti kauliukus, užpilti cukrumi ir palikti 30 minučių.\n                                                                Per tą laiką uogas keletą kartų pamaišyti, kad susidarytų daugiau sulčių, kurias reikia nupilti, o vėliau supilti į gatavą kisielių.\n                                                                Uogų kauliukus užpilti 2,5 stiklinės karšto vandens, užvirinti ir iškošti.\n                                                                Į verdantį nuovirą su uogomis supilti išleistą bulvių krakmolą, vėl pakaitinti iki virimo, nukelti nuo ugnies.\n                                                                Į gatavą kisielių supilti uogų sultis ir išmaišyti.&lt;/p&gt;', '4026.jpg'),
(197, 'Kavos ir sulčių receptas', '&lt;p&gt;Kava ir sultis išplakame. Cukrų įmaišome į kavą arba į gautą plakinį.&lt;/p&gt;&lt;p&gt;Į taures įdedame ledų.&lt;/p&gt;&lt;p&gt;Labai skanus šaltas kokteilis.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '1694.jpg'),
(198, 'Aštrios krevetės grilyje', '&lt;p&gt;Dideliame dubenyje sumaišykite alyvuogių aliejų, česnaką, citrinų sultis, Cajun prieskonius ir petražoles, sezamo sėklas su druska ir pipirais. Sudėkite krevetes ir pusvalandžiui dėkite į šaldytuvą.\n                                                                Ant iešmelių pakaitomis verkite krevetes ir daržoves. Kiekviename iešmelyje turėtų tilpti apie 3 krevetes. Kepkite grilyje apie 3 minutes, kol krevetės taps rausvos spalvos.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '4212.jpg'),
(199, 'Moliūgo salotos', '&lt;p&gt;Moliūgo netarkuoti, supjaustyti. Susmulkinti kitus produktus, sumaišyti sojų padažą su majonezu ir užlieti ant salotų, įberti pipirų, paprikos miltelių ir išmaišyti.&lt;/p&gt;', 'no-photo-280x180.jpg'),
(200, 'Varškės apkepėlis', '&lt;p&gt;Išplakite produktus.&lt;/p&gt;&lt;p&gt;Pirmiausiai išplakite kiaušinius su cukrumi ir vaniliniu cukrumi.&lt;/p&gt;&lt;p&gt;Tada sudėkite grietinę, manų kruopas, aliejų, sodą (sodą nugesinkite) ir varškę.&lt;/p&gt;&lt;p&gt;Galite pridėti razinų, abrikosų.&lt;/p&gt;&lt;p&gt;Kepkite 180 laipsnių orkaitėje 45-50 minučių.&lt;/p&gt;', '2193.jpg'),
(201, 'Vištiena su bulvių koše', '&lt;p&gt;Bulves uždedame virti. Vištienos gabaliukus paprieskoniuojame ir pakepiname. Kitoje keptuvėje pakepinam kopustą pjaustytą juostelėmis ir kukurūzus. Tada į daržoves sudedame vištienos gabaliukus ir dar pakepiname. Į išvirusias bulves įpilame pieno, pasūdome ir sutriname, aš tryniau blenderiu. Tada sudedame į košę daržoves su vištiena, išmaišome. Paskaniname šviežiais agurkiukais ir skanaus!&lt;/p&gt;', '4145.jpg'),
(202, 'Varškės kremas', '&lt;p&gt;Varškė ištrinama pro sietelį, razinos sumalamos. Sviestas ištrinamas su cukrumi ir vanilinu. Viskas gerai sumaišoma ir išplakama.&lt;/p&gt;', '4221.jpg'),
(203, 'Citrinų putėsiai', '&lt;p&gt;Iš citrinų išspaudžiamos sultys. Vanduo, cukrus ir citrinų žievelės bei tirščiai išverdami, perkošiami. Į nuovirą supilamos citrinų sultys ir ištirpinta želatina. Atvėsinus plakama, kol virsta putomis. Masė sukrečiama į formą ir stingdoma. Prieš tiekiantis perverčiama.&lt;/p&gt;', '4478.jpg'),
(204, 'Labai skani braškių sriuba', '&lt;p&gt;Nuplauti uogas, didesnes perpjauti ir sudėti į indą (jei mėgstate trintas, galite sutrinti).&lt;/p&gt;&lt;p&gt;Į puodą pilkite vyną, sirupą arba medų, sudėkite tarkuotą imbierą.&lt;/p&gt;&lt;p&gt;Kaitinkite ant silpnos ugnies, kai pradės virti nukelkite.&lt;/p&gt;&lt;p&gt;Sumeskite pjaustytas mėtas ir leiskite sriubai atvėsti.&lt;/p&gt;&lt;p&gt;Skystį perkoškite ,supilkite ant braškių ir išmaišykite.&lt;/p&gt;&lt;p&gt;Galite pagardinti smulkintais riešutais.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '3801.jpg'),
(205, 'Korėjietiškai marinuoti pomidorai', '&lt;p&gt;Prieskoniai nurodyti 1l stiklainiui. Jei gaminsite didesniuose stiklainiuose, atitinkamai jų padauginkite. Pomidorus su prieskoniais sudėkite į stiklainį ir užpilkite juos virtu marinatu (išvirtu iš marinatui skirtų ingredientų). Stiklainius sustatykite į 80 laipsnių vandens puodą, pridenkite (neužsukite) dangteliais ir kaitinkite 1l stiklainius 20, 3 L stiklainius 30 minučių. Po to dangtelius užsukite, apverskite stiklainius ir leiskite ataušti.&lt;/p&gt;', 'no-photo-280x180.jpg'),
(206, 'Citrinų–pieno gėrimas', '&lt;p&gt;Kiaušinių baltymus išplakti iki putų ir sumaišyti su pienu, cukrumi ir citrinos sultimis bei žievele.&lt;/p&gt;&lt;p&gt;Po to į plakinį įmaišyti kiaušinių trynius.&lt;/p&gt;', '1803.jpg'),
(207, 'Keptos bulvės su grybais', '&lt;p&gt;Džiovintus grybus išmirkyti 1/4 l šilto vandens. Svogūnus supjaustyti kubeliais ir pakepinti su sviestu. Sudėti grybus, supilti vandenį, kuriame mirko grybai, ir 10 minučių patroškinti.&lt;/p&gt;&lt;p&gt;Pievagrybius nuplauti šaltu vandeniu, supjaustyti ir iškart suberti į keptuvę su grybais ir svogūnais. Pagardinti druska ir pipirais, užvirinti, paskui nuvarvinti sietelyje. Grybų nuovirą virti, kol liks 1/8 l skysčio.&lt;/p&gt;&lt;p&gt;Į nuovirą sukrėsti grietinę, užvirinti. Suversti griežinėliais supjaustytas bulves ir 5 minutes pavirti. Pusę bulvių suversti į kepimo formą, apdėti grybais ir pridengti likusiomis bulvėmis.&lt;/p&gt;&lt;p&gt;Kiaušinius suplakti, pamažu supilti grybų nuovirą su grietine, suberti petražoles ir pasūdyti. Bulves užpilti paruoštu plakiniu. Apibarstyti tarkuotu sūriu ir apkepti orkaitėje.&lt;/p&gt;', '4840.jpg'),
(208, 'Alaus kokteilis su Cola', '&lt;p&gt;null&lt;/p&gt;', '3904.jpg'),
(209, 'Gaivus desertas', '&lt;p&gt;Nulupti mangą, supjaustyti ir smulkintuvu sutrinti kartu su uogomis, uogiene (tiršta) arba džemu.\n                                                                Jogurtą pagardinti cukrumi ir elektriniu plaktuvu išplakti.\n                                                                Į taures pilti mango ir uogų masę, tada lėtai supilti jogurtą ir papuošti mėtos lapeliu ar plakta grietinėle.&lt;/p&gt;&lt;p&gt;Skanaus ;)&lt;/p&gt;', '5506.jpg'),
(210, 'Šokoladiniai lietiniai', '&lt;p&gt;Kondensuotą pieną praskiesti 2 stiklinėmis verdančio vandens, įdėti miltų, suplakti. Supilti vieną stiklinę šalto vandens, kiaušinius, ištirpintą sviestą, viską gerai išmaišyti ir kepti lietinius ant truputį didesnės nei vidutinė ugnies. Kiekvieną lietinį patepti sviestu. Skanaus!&lt;/p&gt;', '3090.jpg'),
(211, 'Sojos kepsnys su sūriu', '&lt;p&gt;Sojų mėsą paruoškite pagal aprašymą ant dėžutės. Turimus maždaug 4 x 5 cm gabalėlius įpjaukite horizontaliai taip, kad atsiverstų lyg knygos lapas.&lt;/p&gt;&lt;p&gt;Pabarsčius prieskoniais į vidų dėkite po griežinėlį sūrio ir obuolio, šiaudelį paprikos. Suvožkite, apvoliokite džiūvėsėliuose ir įdėkite į įkaitintus riebalus.&lt;/p&gt;&lt;p&gt;Kai viena pusė apkeps, apverskite. Ant viršaus uždėkite dvi siauras sūrio juosteles, o per vidurį - paprikos juostelę. Uždenkite keptuvę ir kepkite, kol kepsnys apkeps, o sūris - išsilydys. Patiekite karštus su bulvėmis ar žalių daržovių salotomis.&lt;/p&gt;', '391.jpg'),
(212, 'Irisiniai saldainiai', '&lt;p&gt;Į puodą supilkite pieną, suberkite cukrų, supilkite medų ir įdėkite gabaliuką sviesto.&lt;/p&gt;&lt;p&gt;Kaitinkite ir visą laiką maišykite, kol užvirs. Po to nukelkite nuo ugnies.&lt;/p&gt;&lt;p&gt;Kai šiek tiek pravės įlašinkite vanilės.&lt;/p&gt;&lt;p&gt;Šią masę supilstykite į formeles ir palikite šaldytuve, kol sukietės.&lt;/p&gt;&lt;p&gt;Aš dažniausi pagaminu iš vakaro, o ryte jau galima valgyti. :)&lt;/p&gt;', '2824.jpg'),
(213, 'Slyvaičių ir mėtų gėrimas', '&lt;p&gt;Džiovintos mėtos užpilamos verdančiu vandeniu, suberiamas cukrus, viskas gerai išmaišoma ir palaikoma 1 valandą.&lt;/p&gt;&lt;p&gt;Po to supilamos slyvų sultys ir viskas gerai išplakama. Gerti gerai atvėsintą.&lt;/p&gt;', '1827.jpg'),
(214, 'Vasariškas želė desertas', '&lt;p&gt;Išbrinkinti želatiną vandenyje (150- 200 ml).\n                                                                Tada želatiną nuolat maišant kaitinti, kol ištirps granulės. Labai nedaug tirpinio įsipilti į atskirą indą (jo dar reikės).\n                                                                Į ištirpintą, karštą želatiną supilti šviežiai spaustas vyšnių sultis (spausti per sietą), berti cukrų, pilti vandenį. Jei cukrus tirpsta nenoriai galima pakaitinti, bet neužvirti.\n                                                                Gautą tirpinį supilti  į formą ir likusią formos dalį užpilti pasiliktu želatinos tirpiniu. Prieš patiekiant formą palaikyti kelias sekundes karštame vandenyje ir apversti tiesiai į lėkštę.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '4008.jpg'),
(215, 'Bulviniai pyragėliai su dešrelėmis', '&lt;p&gt;Išvirkite bulves pasūdytame vandenyje, sudėkite kiaušinio trynį ir miltus ir gerai sumaigykite masę. Iš košės formuokite papločius ir juos įdarykite dešrelėmis. Apvoliokite juos džiūvėsėliuose ir kepkite įkaitintame aliejuje.&lt;/p&gt;', '6483.jpg'),
(216, 'Salotos su liežuviu', '&lt;p&gt;Supjaustykite juostelėmis liežuvį ir agurką.\n                                                                Morkas, bulves , salierus supjaustykite kubeliais.\n                                                                Tada sujunkite ingredientus, pridėkite pipirų , druskos , acto . Užpilkite  salotas aliejumi ir gerai išmaišykite.&lt;/p&gt;&lt;p&gt;Skanaus&lt;/p&gt;', '5633.jpg'),
(217, 'Saldi sriuba su razinomis', '&lt;p&gt;Į puodą su vandeniu suberiame nuplautas razinas ir verdame. Vandeniui užvirus suberiame makaronus. Obuolius, kriaušes susmulkiname ir suberiame į puodą. Verdame apie 5-10 min. Įdedame pagal skonį cukraus ir griežinėliais supjaustytus kivi vaisius. Kai cukrus ištirpsta, sriubą nukeliame nuo ugnies.&lt;/p&gt;&lt;p&gt;Sriubą rekomenduojama valgyti atvėsusią. :)&lt;/p&gt;', '2862.jpg'),
(218, 'Kaukazinių slyvaičių tyrė', '&lt;p&gt;Nuplautos slyvaitės užpilamos trupučiu vandens ir uždengtame puode pavirinamos. Pravėsinta masė pertrinarna per sietelį, pridedama cukraus pagal skonį ir užvirinama. Karšta supilama į paruoštus stiklainius ir sandariai uždaroma. Tokią tyrę galima užkonservuoti ir be cukraus.&lt;/p&gt;&lt;p&gt;Norint, kad tyrė būtų tirštesnė, reikia ilgiau pavirinti.&lt;/p&gt;', '4149.jpg'),
(219, 'Tortilijos tortukas su tunu', '&lt;p&gt;Gaminami 3 pertepimai.&lt;/p&gt;&lt;p&gt;1 pertepimui reikalinga: 2 dėžutės konservuoto tuno, šaukštas majonezo.&lt;/p&gt;&lt;p&gt;2 pertepimui reikalinga: 3 virti kiaušiniai, truputis prieskonių pagal skonį (labai skanu aštresni, pvz. paukštienos), kelios skilteles česnako, majonezo viskam išmaišyti.&lt;/p&gt;&lt;p&gt;3 pertepimui reikalinga: apie 400 g fermentinio sūrio, česnako pagal skonį ir majonezo išmaišyti.&lt;/p&gt;&lt;p&gt;Blynus reikia šiek tiek sutepti majonezu ir sluoksniuoti: blynas, tunas, blynas, sūris ir t.t. Tortą aptepti likusia sūrio ar kiaušinio mase, 4-5 valandas laikyti šaldytuve. Puošti galima alyvuogėmis, marinuotomis smulkiomis daržovėmis.&lt;/p&gt;&lt;p&gt;Labai skanu, pabandykit :)&lt;/p&gt;', '529.jpg'),
(220, 'Keptos cukinijos su mėtų padažu', '&lt;p&gt;Cukinijas nuplaukite,  nupjaukite viršūnėles ir kotelius ir supjaustykite 1 cm storio griežinėliais.\n                                                                Kiaušinį suplakite su žiupsneliu druskos. Apvoliokite cukinijų griežinėlius miltuose, paskui - kiaušinio plakinyje ir džiūvėsėliuose.\n                                                                Apkepkite iš abiejų pusių aliejuje su sviestu, kol gražiai pagelsd. Sudėkite ant popierinių servetėlių, kad susigertų aliejus.\n                                                                Į puodą sudėkite mėtas, suberkite cukrų, supilkite vyno actą ir pakaitinkite iki užvirimo.\n                                                                Cukinijas sudėkite į lėkšte ir apšlakstykite mėtų padažu.&lt;/p&gt;&lt;p&gt;Šaltinis: Cristine Bier &quot;Gardžiausi cukinijų patiekalai&quot;&lt;/p&gt;', '6538.jpg'),
(221, 'Vafliai su graikiniais riešutais', '&lt;p&gt;Gerai išplakite sviestą, kiaušinius, cukrų ir miltus. Iškepkite vaflių keptuvėjė. Susukite į vamzdelius.\n                                                                Ištirpinkite šokoladą  iki vienalytės masės, sumaišykite su medumi ir suberkite graikinius riešutus.\n                                                                Įdaru užpildykite vaflių vamzdelius.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '4056.jpg'),
(222, 'Kava su brendžiu', '&lt;p&gt;Kavą užplikinti 95 C vandeniu, palaikyti 2-3 min. ir nukošti.&lt;/p&gt;&lt;p&gt;Į kavą įmaišyti brendį, likerį ir cukrų.&lt;/p&gt;', '1817.jpg'),
(223, 'Rauginti agurkai su vynuogių lapais', '&lt;p&gt;Agurkus, nupjovus galiukus,  apie 2 valandas reikėtų pamirkyti šaltam vandeny. Tuo metu gerai išplauname ir iškaitiname reikiamą kiekį litrinių stiklainių. Taip pat priskiname vynuogių lapų ir juos nuplikome verdančiu vandeniu.&lt;/p&gt;&lt;p&gt;Į stiklainius dedame po 5-6 vynuogių lapus, nubrauktą šaukštą druskos, skiltelę česnako ir arbatinį šaukštelį prieskonių rauginimui mišinio. Sandariai sugrūdame agurkus, iki viršaus užpilame šaltu (šulinio, gręžinio, šaltinio) vandeniu, uždarome pakaitintais plastikiniais dangteliais ir kuo skubiau nešam į vėsią patalpą. Valgyti juos galima tik po kelių mėnesių, bet traškumas ir skonis tikrai verti palaukti ir kelis mėnesius.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '3426.jpg'),
(224, 'Sorų kruopų košė', '&lt;p&gt;Kruopos perrenkamos, nuplaunamos ir nuplikomos verdančiu vandeniu. Tada sudedamos į verdantį pasūdytą vandenį ir pamažu verdamos, kol košė pradės tirštėti. Tada supilamas pienas, sudedamas sviestas, pasūdoma, įberiama truputį cukraus ir maišant, kad neprisviltų, išverdama.&lt;/p&gt;&lt;p&gt;Valgoma su pienu, kompotu arba kokia nors uogiene.&lt;/p&gt;', '1121.jpg'),
(225, 'Bulvių apkepas pagal Vanill', '&lt;p&gt;Bulves supjaustome griežinėliais ir išklojame kepimo indą,pabarstome prieskoniais. Ant viršaus sudedame sluoksnį supjaustytos šoninės. Tuomet vėl dedam sluoksnį bulvių, šoninės ir t.t Užpilame grietinėlę ir pabarstome tarkuotu sūriu. Dedame į įkaitintą 200 laipsnių orkaitę ir kepame maždaug 30-40 min.&lt;/p&gt;', '1019.jpg'),
(226, 'Kavos ir „coco-colos“ kokteilis', '&lt;p&gt;„Coca-cola“ gėrimo reikės apie 400 g.&lt;/p&gt;&lt;p&gt;Kavą išplakite su „coca-cola“ gėrimu. Ant kavos sudėkite ledus. Galite įmesti ledo kubelių.&lt;/p&gt;&lt;p&gt;Labai gaivus gėrimas.&lt;/p&gt;', '2254.jpg'),
(227, 'MARTINI MELOGRANO', '&lt;p&gt;Sumaišomas Martini Rosato ir granatų sultys.&lt;/p&gt;&lt;p&gt;Patiekiama aukštoje taurėje su ledukais, puošiama mėta.&lt;/p&gt;', '3386.jpg'),
(228, 'Bulvių košė', '&lt;p&gt;Į pasūdytą, verdantį vandenį sudėti smulkiai supjaustytas bulves. Kai vanduo vėl užvirs, įdėti supjaustytą svogūną. Virti apie 15 minučių.&lt;/p&gt;&lt;p&gt;Išvirus, nupilti vandenį, sugrūsti ir įmaišyti grietinę.&lt;/p&gt;', '3207.jpg'),
(229, 'Vyšnių gira', '&lt;p&gt;Apyniai pavirinami vandenyje 15 min. ir iškošiami.&lt;/p&gt;&lt;p&gt;Vyšnios išspaudžiamos ir sultys išpilstomos į ataušintą vandenį su apyniais. Suberiamas cukrus, sudedamos pakildytos mielės, parauginama apie 12-24 val. ir išpilstoma į butelius.&lt;/p&gt;', '1680.jpg'),
(230, 'Karšti sumuštiniai su šprotais', '&lt;p&gt;Batoną supjaustome riekelėmis.&lt;/p&gt;&lt;p&gt;Kiekvieną batono riekelę patepame majonezu ir dedame į kepimo spardą.&lt;/p&gt;&lt;p&gt;Ant kiekvienos riekelės užtepame sutrintų šprotų masės (šprotus aliejuje sutriname iki vientisos masės, kad gautųsi košelė).&lt;/p&gt;&lt;p&gt;Pabarstome sūriu ir kepame orkaitėje 6-10 min.&lt;/p&gt;&lt;p&gt;Orkaitę įkaitinti iki 160-180 laipsnių temperatūros.&lt;/p&gt;', '1983.jpg'),
(231, 'Špinatų tyrė', '&lt;p&gt;Špinatai ne ilgiau kaip 5 min. apverdami atidengtame puode. Vėliau išgriebiami ir ištrinami pro sietelį. Miltai pakepinami svieste, atskiedžiami stikline špinatų nuoviro ir verdama, kol sutirštėja. Padažas užpilamas ant špinatų tyrės.&lt;/p&gt;', '4147.jpg'),
(232, 'Bananų-apelsinų kokteilis', '&lt;p&gt;Nulupame bananus ir supjaustome mažais gabalėliais. Šakute sutriname juos dubenyje.&lt;/p&gt;&lt;p&gt;Perpjauname apelsinus pusiau ir išspaudžiame sultis.&lt;/p&gt;&lt;p&gt;Bananų tyrę, apelsinų sultis ir pieną suplakame (galima naudoti elektrinį ar paprastą plaktuvą).&lt;/p&gt;&lt;p&gt;Bananų kokteilį išpilstome į keturias stiklines ir sudedame vanilinius ledus. Galima kokteilį ir ledus suplakti ir tik tada tiekti. Skanaus!&lt;/p&gt;', '1704.jpg'),
(233, 'Arbūzinis punšas', '&lt;p&gt;Arbūzo minkštimą išvalyti (pašalinti sėklas) ir supjaustyti kubeliais. Avietes sutrinti. Arbūzus išmaišyti su avietėmis.&lt;/p&gt;&lt;p&gt;Užpilti putojančiu vynu.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '2170.jpg'),
(234, 'Biri perlinių kruopų košė', '&lt;p&gt;Į verdantį pasūdytą vandenį sudedami riebalai ir suberiamos kruopos. Užverdama maišant. Tada puodas uždengiamas, įstatomas į didesnį indą su karštu vandeniu, ir taip verdama apie 2 val. Išgaravęs vanduo papildomas.&lt;/p&gt;', '4190.jpg'),
(235, 'Saldžiarūgštis padažas žiemai', '&lt;p&gt;Pašalinkite paprikų sėklas ir supjaustykite jas maždaug 1 x 1 cm dydžio kubeliais. Nulupkite cukinijų odą, perpjaukite daržoves per pusę išilgai ir susmulkinkite tokio pat dydžio kubeliais. Paprikas ir cukinijas dėkite į puodą, užpilkite pomidorų padažu ir actu, berkite cukrų, pagardinkite druska ir troškinkite nedidelėje kaitroje nuolat maišydami apie 45-60 minučių. Suminkštėjus daržovėms, pilstyti į stiklainius. Man gaunasi apie 4 litrai. Skanaus :)&lt;/p&gt;', '5184.jpg'),
(236, 'Burokėlių gira', '&lt;p&gt;Burokus nuplauti, nulupti, perplauti, supjaustyti plonais griežinėliais ir sudėti į švarų stiklainį.\n                                                                Užbarstyti cukrumi, druska, užpilti atvėsintu virintu vandeniu, sudėti duonos plutas.\n                                                                Indą uždengti marle ir pastatyti šiltai 5 paroms. Tada girą iškošti per marlę ir išpilstyti į sterilizuotus stiklainius.&lt;/p&gt;', '4023.jpg'),
(237, 'Bananai šokolade', '&lt;p&gt;Bananus nulupti ir supjaustyti į tris dalis ir įsmeigti pagaliuką.\n                                                                Riešutus susmulkinti ir paskrudinti keptuvėje , ant mažos ugnies iki auksinės spalvos.\n                                                                Ištirpinti šokoladą ir leisti masei pravėsti. Imti bananus ant pagaliuko, pamirkyti šokolade ir apvolioti riešutuose.&lt;/p&gt;&lt;p&gt;Paprasta ir labai skanu!&lt;/p&gt;', '6417.jpg'),
(238, 'Skrudintos duonos sumuštiniai', '&lt;p&gt;Ant duonos trikampėlių iš abiejų pusių tepkite svarainių džemo, tada dėkite sūrio ir kepkite ištirpintame svieste iš abiejų pusių (kepdami paspauskite sumuštinius mentele, jog gerai suliptų). Kiek pravėsusius sumuštinukus perverkite dantų krapštukais. Patiekite šiltus.&lt;/p&gt;', '4267.jpg'),
(239, 'Žuvies užkepėlė su daržovėmis', '&lt;p&gt;Įkaitinkite orkaitę iki 180° C.&lt;/p&gt;&lt;p&gt;Kepimo skardą ištepkite sviestu.&lt;/p&gt;&lt;p&gt;Ant dugno sudėkite žuvies gabaliukus.&lt;/p&gt;&lt;p&gt;Ant žuvies sudėkite bulvių ir morkų gabaliukus.&lt;/p&gt;&lt;p&gt;Pieną išmaišykite su majonezu ir prieskoniais.&lt;/p&gt;&lt;p&gt;Pieno masę supilkite į skardą. Viršų apibarstykite sūriu ir krapais.&lt;/p&gt;&lt;p&gt;Kepkite apie 25-30 minučių.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '2798.jpg'),
(240, 'Omletas su grybais', '&lt;p&gt;Svogūną supjaustyti ir apkepti įkaitintame aliejuje iki ausinės spalvos. Tada į keptuvę sudėti pusiau perpjautus pievagrybius ir kepti minutę laiko.\n                                                                Kiaušinius išplakti su žiupsneliu druskos. Keptuvėje ištirpinti sviestą ir supilti kiaušinių plakinį ir kepti pora minučių. Tada, vienoje omleto pusėje, sudėti keptuvės turinį, mentele perlenkti omletą ir dar keletą minučių kepti.&lt;/p&gt;', '6401.jpg'),
(241, 'Silkės salotos', '&lt;p&gt;Smulkiai supjaustyti ar sutarkuoti kopūstus, salotas, plonyčiais gabaliukais supjaustyti silkę, griežinėliais obuolius. Dėti smulkintus krapus, barstyti baltųjų pipirų, druskos, sudėti grietinę, kupiną arbatinį šaukštelį garstyčių, pilti pora šaukštelių marinato iš silkės, berti žiupsnelį druskos ir gerai viską išmaišyti.&lt;/p&gt;&lt;p&gt;Labai skanu :)&lt;/p&gt;', '4488.jpg'),
(242, 'Varškės ir moliūgų apkepas', '&lt;p&gt;Manus užpilkite keliais šaukštais verdančio vandens.&lt;/p&gt;&lt;p&gt;Į pieną sudėkite sviestą, kubeliais supjaustytą moliūgą ir virkite apie 10 minučių.&lt;/p&gt;&lt;p&gt;Moliūgus sutrinkite ir sumaišykite su manais.&lt;/p&gt;&lt;p&gt;Varškę pertrinkite per sietelį ir įmaišykite į manų ir moliūgų mišinį.&lt;/p&gt;&lt;p&gt;Kiaušinius išplakite su cukrumi ir kitais prieskoniais ir įmaišykite į moliūgų masę.&lt;/p&gt;&lt;p&gt;Apkepo tešlą sukrėskite į kepimo formą ir kepkite apie 30 min. 200° C orkaitėje.&lt;/p&gt;&lt;p&gt;Kepimo laikas priklauso nuo to, kokio storio jūsų apkepas gausis. Kuo plonesnis, tuo mažiau laiko reikės kepimui.&lt;/p&gt;', '3199.jpg'),
(243, 'Kiaulienos troškinys su ryžiais', '&lt;p&gt;Supjaustome papriką plonomis juostelėmis,  svogūną smulkiais kubeliais. Sudedame juos į keptuvę, įkaitintą svieste. Pakepiname kelias minutes, tuomet sudedame smulkiai pjaustytą česnaką. Viską pakepinę porą minučių, išimame iš keptuvės. Keptuvėje patroškiname pusvalandį kubeliais supjaustytą kiaulieną. Tuo metu, kol troškinasi kiauliena, išverdame ryžius sultinyje, nedaugiau kaip 15 min. Patroškintą kiaulieną sudedame į ryžių puodą, sudedame pakeptas daržoves ir troškiname dar 15 min. Kai viskas išsitroškina, įdedame adžikos padažo, maždaug 250 g ir po 5 min galima patiekti ant stalo. Skanaus!&lt;/p&gt;', '4162.jpg'),
(244, 'Putojančio vyno kokteilis "Blood Orange"', '&lt;p&gt;Putojančio vyno ir sulčių santykis turi būti vienodas 1:1. Šviežiai spaustas sultis sumaišykite su putojančiu vynu.&lt;/p&gt;&lt;p&gt;Salute :)&lt;/p&gt;', '3562.jpg'),
(245, 'Varškės virtinukai', '&lt;p&gt;Varškę pertrinti per sietelį (galite pirkti jau sutrintą) ir sumaišyti su miltais, kiaušiniais, druska ir cukrumi. Išminkyti tešlą (0,5- 0,8 cm storio).&lt;/p&gt;&lt;p&gt;Formelių pagalba  formuoti virtinukus , tada dėti į užvirusį vandenį ir virti ant mažos ugnies 4- 5 minutes.&lt;/p&gt;&lt;p&gt;Pagamintus ir neišvirtus virtinukus, galima šaldyti ir kitą kartą tereikės tik išvirti :)&lt;/p&gt;', '3980.jpg'),
(246, 'Naminiai čipsai su avokado padažu', '&lt;p&gt;Avokadą sutrinkite, įspauskite šiek tiek citrinų sulčių. Sudėkite majonezą, susmulkintą česnaką ir petražoles. Pagardinkite druska ir pipirais ir viską gerai išmaišykite.\n                                                                Bulves nuskuskite ir supjaustykite plonais griežinėliais. Puode arba keptuvėje įkaitinkite aliejų iki 160°C laipsnių ir gruzdinkite bulvių griežinėlius, kol jie pasidarys traškūs ir taps auksinės rudos spalvos. Nusausinkite bulves popieriniu rankšluosčiu ir patiekite kartu su avokadų padažu.&lt;/p&gt;&lt;p&gt;Tokius čipsus pasigaminti galite ir iš kitų daržovių.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '4411.jpg'),
(247, 'Šventinis keksas', '&lt;p&gt;Išmaišykite jogurte arba pasukose sodą.&lt;/p&gt;&lt;p&gt;Išplakite kiaušinį su cukrumi.&lt;/p&gt;&lt;p&gt;Į kiaušinio plakinį įmaišykite jogurtą ir miltus.&lt;/p&gt;&lt;p&gt;Įmaišykite razinų arba aguonų pagal skonį.&lt;/p&gt;&lt;p&gt;Tešlą supilkite į kepimo formą.&lt;/p&gt;&lt;p&gt;Įkaitinkite orkaitę iki 180 laipsnių.&lt;/p&gt;&lt;p&gt;Kepkite apie 50-60 min.&lt;/p&gt;', '2643.jpg'),
(248, 'Žirnelių salotos su kumpiu', '&lt;p&gt;Viską supjaustyti norima forma (kartais pjaustau kubeliais, o kartais - pailgais griežinėliais), sudėti prieskonių ir majonezo bei viską išmaišyti.&lt;/p&gt;', '5301.jpg'),
(249, 'Troškinys su soja (mėsa)', '&lt;p&gt;Pirmiausia pakepinu mėsytę (3-4 min.), dedu prieskonių pagal skonį. Pakepinus dedu į puodą, sudedu visus ingredientus ir užpilu 3-4 stiklinėmis vandens.&lt;/p&gt;&lt;p&gt;Viską troškinu apie 1 valandą. Prieš pat baigiant troškintis sudedu pupeles ir kečupą.&lt;/p&gt;&lt;p&gt;Tiesa, troškinį galima gaminti su soja-tada gaunasi vegetariškas patiekalas. Greita ir skanu :)&lt;/p&gt;', '2602.jpg'),
(250, 'Pieniška grikių sriuba', '&lt;p&gt;Grikių kruopas perplauname tekančiu vandeniu ir 1 val. mirkome virintame vandenyje.&lt;/p&gt;&lt;p&gt;Užviriname 1,5 stiklinės vandens, jį pasūdome ir verdame grikius apie 15 min. Įdedame medų, supilame pieną ir kaitiname. Kai pienas pradeda kilti, įdedame sviesto ir nukaičiame puodą. 5 min. jį palaikome uždengtą.&lt;/p&gt;', '3213.jpg'),
(251, 'Arvydo geriamos sveikatingumo salotos', '&lt;p&gt;Pusę pomidorų sulčių įpilama į mikserį (blenderį). Įdedamas nuluptas avokadas (gali būti ir su kauliuku, kuris yra gera viduriavimo stabdymo priemonė), plakama iki vienalytės masės. Tada palaipsniui dedama visa kita, kaskart gerai išplakant.&lt;/p&gt;&lt;p&gt;Gerti atvėsintas.&lt;/p&gt;&lt;p&gt;Galimos pačios įvairiausios daržovių variacijos.&lt;/p&gt;', '1702.jpg'),
(252, 'Tuno blyneliai', '&lt;p&gt;Sutarkuokite morkas.&lt;/p&gt;&lt;p&gt;Svogūnus sukapokite. Salierus taip pat supjaustykite.&lt;/p&gt;&lt;p&gt;Morkas, svogūną ir salierus pakepinkite aliejuje 2-3 minutes.&lt;/p&gt;&lt;p&gt;Viską sudėti į vieną dubenėlį ir sumaišyti.&lt;/p&gt;&lt;p&gt;Iš gautos masės kepti blynus.&lt;/p&gt;&lt;p&gt;Blynelius kepti keptuvėje. Kepti po 1-2 minutes, kiekvieno blyno pusę.&lt;/p&gt;', '2421.jpg'),
(253, 'Vieno kąsnio silkės sumuštiniai', '&lt;p&gt;Juodą duoną supjaustykite kvadratėliais 5x5 cm.&lt;/p&gt;&lt;p&gt;Supjaustykite silkę 2 cm pločio gabaliukais.&lt;/p&gt;&lt;p&gt;Obuolį ir burokėlius sutarkuokite ir sutrinkite iki tyrelės konsistencijos.&lt;/p&gt;&lt;p&gt;Į burokėlių ir obuolių košelę įmaišykite majonezą ir sūrį.&lt;/p&gt;&lt;p&gt;Ant kiekvieno duonos gabaliuko užtepkite burokėlių košės, o ant košelės uždėkite silkės gabaliuką.&lt;/p&gt;&lt;p&gt;Persmeikite kiekvieną sumuštinuką mediniu pagaliuku.&lt;/p&gt;&lt;p&gt;Ant pagaliuko užmaukite svogūno laišką.&lt;/p&gt;', '2451.jpg'),
(254, 'Agurkų padažas', '&lt;p&gt;Šviežius agurkus sutarkuojame, česnaką sutriname arba labai smulkiai sutarkuojame.&lt;/p&gt;&lt;p&gt;Krapus smulkiai sukapojame.&lt;/p&gt;&lt;p&gt;Į majonezą įmaišome krapus, agurkus ir česnakus.&lt;/p&gt;&lt;p&gt;Skanus padažas tinkantis prie mėsos, bulvių ir t.t. :)&lt;/p&gt;', '3478.jpg'),
(255, 'Troškinti daržovių suktinukai', '&lt;p&gt;Šviežias daržoves ir pievagrybius patroškinti aliejuje, kad suminkštėtų.&lt;/p&gt;&lt;p&gt;Daržoves sudėti ant lavašo ir susukti.&lt;/p&gt;&lt;p&gt;Supjaustyti griežinėliais. Griežinėlius sudėti į kepimo skardą ir užpilti padažu.&lt;/p&gt;&lt;p&gt;Padažas: į verdantį vandenį sudėti prieskonius ir pomidorų padažą, gerai išmaišyti.&lt;/p&gt;&lt;p&gt;Kepti 180-200 laipsnių orkaitėje 20 minučių.&lt;/p&gt;&lt;p&gt;Tiekite su grietinėlės ar grietinės padažu ir žalumynais.&lt;/p&gt;', '1885.jpg'),
(258, 'Marinuoti agurkai su svogūnais', '&lt;p&gt;Visus produktus sudėti į trijų litrų stiklainį, užpilti verdančiu vandeniu, palaikyti 20 min, po to nupilti ir dar kartą užvirinti ir užpilti.  Po to sandariai uždaryti.&lt;/p&gt;', '4089.jpg'),
(259, 'Apkepas „La Graikija“', '&lt;p&gt;Kiaulienos faršą išmaišykite su prieskoniais ir sudėkite į kepimo formą.&lt;/p&gt;&lt;p&gt;Ant faršo sudėkite supjaustytus porų, bulvių, paprikos, pomidorų, baklažanų griežinėlius. Viską aptepkite majonezu ir užbarstykite sutarkuotu sūriu.&lt;/p&gt;&lt;p&gt;Kepkite apie 50 minučių iki 180° C įkaitintoje orkaitėje.&lt;/p&gt;', '2933.jpg'),
(260, 'Ledų kokteilis', '&lt;p&gt;Bananus, braškes ir ledus išplakti plaktuvu.\n                                                                Pilti sultis, pieną ir viską dar kartą išplakti.&lt;/p&gt;', '3786.jpg'),
(261, 'Silkės ruletai', '&lt;p&gt;Silkes paguldykite į indą su citrinų sultimis ir sojų padažu. Ant silkių gausiai berkite druskos ir palikite 2 - 3 valandoms marinuotis. Silkę supjaustykite mažesniais gabaliukais ir susukite suktinukus, perverkite juos dantų krapštuku.&lt;/p&gt;', '5584.jpg'),
(262, 'Spanguolių gėrimas', '&lt;p&gt;Citriną supjaustyti kubeliais.&lt;/p&gt;&lt;p&gt;Citriną, trintas spanguoles ir cukrų užpilti užvirintu vandeniu.&lt;/p&gt;&lt;p&gt;Išmaišyti, palaikyti 5 min. ir nukošti.&lt;/p&gt;&lt;p&gt;Gerti atšaldytą.&lt;/p&gt;', '1805.jpg'),
(263, 'Daržovių sriuba', '&lt;p&gt;Daržoves (išskyrus pomidorus) nulupkite ir supjaustykite. Pomidorus truputį įpjaukite, sudėkite kelioms minutėms į verdantį vandenį, nusausinkite ir nuimkite odelę. Visas daržoves pakaitinkite puode, įpylę aliejaus. Supilkite verdantį vandenį, sudėkite sultinio kubelius ir virkite 20-30 minučių. Viską sutrinkite, įberkite druskos, pipirų ir atvėsinkite. Sudėkite susmulkintas petražoles, prieš patiekdami įpilkite mineralinio vandens. Skanaus!&lt;/p&gt;', '1578.jpg'),
(264, 'Makaronų apkepas su obuoliais', '&lt;p&gt;Makaronus išvirti pasūdytame vandenyje, išgriebus nuvarvinti ir perpilti šaltu vandeniu. Iš obuolių išimti sėklalizdžius ir supjaustyti nedideliais &amp;nbsp;gabaliukais. Į grietinę įmušti kiaušinį, suberti cukrų ir išplakti. Į riebalais išteptą skardą dėti makaronus, obuolius, migdolus, razinas. Užpilus grietinės ir kiaušinių plakinių, kepti 15-20 minučių 200C karštumo orkaitėje. Patiekiant apibarstyti cinamonu. Skanaus!&lt;/p&gt;', '3947.jpg'),
(265, 'Pipirnės salotos', '&lt;p&gt;Šviežius lapus nuplauti, smulkiai supjaustyti, sumaišyti su susmulkintais svogūnų laiškais, krapų ir petražolių lapais, pasūdyti ir pagardinti grietine.&lt;/p&gt;', '5379.jpg'),
(266, 'Grybų troškinys moliniame inde', '&lt;p&gt;Nuplauname šviežius grybus ir supjaustome.\n                                                                Brokolius ir morkas verdame pasūdytame vandenyje 10 - 15 minučių.\n                                                                Grybus apkepiname įkaitintame sviesto skonio &quot;Obelių&quot; aliejuje.\n                                                                Į molinius  indus pilame truputį aliejaus, lygiomis dalimis dedame apkeptus grybus, grietinę, daržoves, vėl dedame grybų. Kepame karštoje orkaitėje apie 40 minučių. Prieš baigiant kepti, viršų pabarstome tarkuotu sūriu, žalumynais (jei norite, kad troškinys būtų sultingesnis ant viršaus, prieš baigiant kepti, galima dėti pjaustytų pomidorų).&lt;/p&gt;&lt;p&gt;Įvairūs grybų troškiniai buvo populiarūs Lenkijos ir Lietuvos bajoriškoje virtuvėje.&lt;/p&gt;&lt;p&gt;Autorės receptas&lt;/p&gt;', '6566.jpg'),
(267, 'Grikių duona', '&lt;p&gt;Visus grūdus sumalame iki miltų.&lt;/p&gt;&lt;p&gt;Mieles sumaišome su medumi bei vandeniu.&lt;/p&gt;&lt;p&gt;Sumaišome visas sudedamąsias dalis ir minkome tešlą. Ją paliekame pakilti 15 minučių.&lt;/p&gt;&lt;p&gt;Tešlą dedame į sviestu ištepta kepimo formą, apibarstome avižiniais dribsniais ir dar paliekame pakilti apie 1 valandą laiko.&lt;/p&gt;&lt;p&gt;Orkaitė įkaitinama iki 220 C laipsnių. Duoną kepame 40 minučių.&lt;/p&gt;', '1447.jpg'),
(268, 'MARTINI ROCKS', '&lt;p&gt;Sumaišomas Martini Rosso ir apelsinų sultys.&lt;/p&gt;&lt;p&gt;Patiekiama žemoje taurėje su ledukais.&lt;/p&gt;', '3396.jpg'),
(269, '„Greipfrutinis mimosa“', '&lt;p&gt;Išspauskite iš greipfrutų sultis ir užpilkite putojančiu vynu.&lt;/p&gt;&lt;p&gt;Į sveikatą :)&lt;/p&gt;', '3549.jpg'),
(270, 'Džiovintų vaisių saldainiai', '&lt;p&gt;Džiovintus vaisius, riešutus sutrintu smulkintuvu ir sumaišyti su medumi (masė turi būti lipni, ne per skysta).\n                                                                Formuoti kukuliukus ir juos įvynioti į maistinę plėvelę. Padėti į šaldytuvą pusvalandžiui.\n                                                                Pusė saldainių gausiai apvolioti kakavoje, kitus cukraus pudroje.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '5527.jpg'),
(271, 'Skanus varškės apkepas', '&lt;p&gt;Varškę, kiaušinių trynius, manų kruopas, cukrų, druską, vanilę ir cinamoną išplakti su mikseriu.&lt;/p&gt;&lt;p&gt;Į varškės masę įmaišyti išplaktus baltymus.&lt;/p&gt;&lt;p&gt;Varškę supilti į kepimo formą. Viršų pabarstyti pabarstukais, kokoso drožlėmis ar razinomis.&lt;/p&gt;&lt;p&gt;Kepti 180 laipsnių orkaitėje 40-50 minučių.&lt;/p&gt;&lt;p&gt;Skanaus :)&lt;/p&gt;', '1995.jpg'),
(272, 'Vištienos krūtinėlės steikas su keptu kopūstu', '&lt;p&gt;Vištienos krūtinėles įtrinti aliejumi ir kepsnių prieskoniais. Nuo kopūsto atpjauti dvi riekes (pjauti per visa galvą apie 1,5 cm storio). Pabarstyti druska, čili pipirais ir apšlakstyti aliejumi. Perverti  pagaliukais, kad neiširtų.\n                                                                Įkaitinti kepsnių keptuvę ir sudėti kepti krūtinėlę ir kopūstus.  Kepti apie 5 min. iš abiejų pusių.&lt;/p&gt;&lt;p&gt;Pietų Vokietijos bajoriškas patiekalas (iš Vokiško reklaminio žurnalo). Kadangi Lietuvos bajorai vykdydavo kulinarinius mainus, tikėtina, kad ir šis patiekalas puikavosi ant Lietuvos didikų stalo.&lt;/p&gt;', '6539.jpg'),
(273, 'Lietiniai blynai su vištiena', '&lt;p&gt;Iš tešlai skirtų produktų išmaišome tešlą ir iškepame lietinius blynus (ne mažo diametro, kad būtų įmanoma gerai sulankstyti). Paprikas pasmulkiname, svogūną kubeliais ir kepame keptuvėje, vėliau dedame virtą kubeliais pjaustytą mėsytę ir paskaniname prieskoniais. Į lietinį blyną dedame įdaro (kiekis pagal skonį) ir sulankstome. Tuomet blyną su įdaru merkiame į kiaušinio plakinį, į džiūvėsėlius, vėl į kiaušinio plakinį, į džiūvėsėlius ir kepame keptuvėje, kol gražiai apskrus. Galima patiekti su česnakiniu padažu arba tiesiog su pomidorų padažu. Skanaus :)&lt;/p&gt;', '3972.jpg'),
(274, 'Greitas makaronų troškinys', '&lt;p&gt;Makaronus išvirti pasūdytame vandenyje su trupučiu aliejaus.\n                                                                Vištieną, nuplauti, nusausinti, supjaustyti gabaliukais ir sudėti kepti į įkaitintą aliejų. Tada sudėti smulkintus svogūnus ir kepti iki auskinės spalvos.\n                                                                Į keptuvę su svogūnais ir vištiena sudėti išvirtus makaronus, barstyti pipirų, sudėti smulkintus žalumynus ir išmaišyti. Viršų pabarstyti tarkuotu kietuoju sūriu.&lt;/p&gt;&lt;p&gt;Skanaus.&lt;/p&gt;', '6423.jpg'),
(275, 'Airiška kava', '&lt;p&gt;Vandenį užvirinkite ir supilkite ant kavos ir cukraus. Nukoškite.&lt;/p&gt;&lt;p&gt;Kavą užpilkite plakta grietinėle, o ant grietinėlės supilkite viskį.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '2135.jpg'),
(276, 'Persikų desertas', '&lt;p&gt;Į nedidelį puodą sudėkite persikus, avietes, cukrų ir vandenį bei užvirkite.&lt;/p&gt;&lt;p&gt;Sumažinkite ugnį ir troškinkite 5 minutes.&lt;/p&gt;&lt;p&gt;Jei norite, atšaldykite.&lt;/p&gt;&lt;p&gt;Patiekite su ledais.&lt;/p&gt;', '4429.jpg'),
(277, 'Vištienos ir moliūgų apkepėlė', '&lt;p&gt;Vištieną supjaustyti kubeliais, pagardinti druska, smulkintais apyniais ir palikti 30 minučių marinuotis.\n                                                                Moliūgo minkštimą supjaustyti ir sudėti į dubenį. Sudėti moliūgų sėklas, berti truputį druskos ir apynių. Palikti marinuotis 30 minučių.\n                                                                Stambiais kubeliais supjaustyti pelėsinį sūrį.\n                                                                Į kepimo indą, pateptą alyvuogių aliejum, sluoksniais sudėti produktus. Pirmiausia sudėti vištieną, tada moliūgus ir pelėsinį sūrį. Viską apšlakstyti aliejumi.\n                                                                Apkepą pašauti į įkaitintą iki 180°C temperatūros orkaitę ir kepti 40 - 50 minučių.&lt;/p&gt;', '6396.jpg'),
(278, 'Naminiai riestainiukai', '&lt;p&gt;Kondensuotą pieną pašildome, kad būtų skystesnis.\n                                                                Įmaišome išplaktus kiaušinių trynius (1 pasiliekame aptepimui), vanilę, cinamoną, druską, sodą ir ištirpintą sviestą.\n                                                                Beriame miltų ir maišome. Kai tešla nebesimaišo su šaukštu baigiami minkyti rankomis.\n                                                                Gerai išminkytą tešlą padaliname į kelias dalis (aš padalinau į 5 dalis) ir išsukame plonus volelius. Supjaustome gabaliukais ir suformuojame riestainiuko formas.\n                                                                Sudedame į kepimo skardą, aptepame kiaušinio trynio plakiniu ir kepame 10 - 15 min.  180-190° C orkaitėje.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '3217.jpg'),
(279, 'Vištienos ir briuselio kopūstų apkepas', '&lt;p&gt;Užpilkite vištieną vandeniu ir virkite 20 minučių.&lt;/p&gt;&lt;p&gt;Briuselio kopūstus pavirkite 10-15 minučių.&lt;/p&gt;&lt;p&gt;Vištieną supjaustykite kubeliais.&lt;/p&gt;&lt;p&gt;Ištirpinkite sviestą, įmaišykite miltus. Pakepinkite 2-3 minutes.&lt;/p&gt;&lt;p&gt;Iš lėto įmaišykite pieną. Įmaišykite prieskonių ir kaitinkite, kol užvirs.&lt;/p&gt;&lt;p&gt;Vištieną ir kopūstus įmaišykite į padažą. Sudėkite viską į kepimo indą, apibarstykite sūriu ir kepkite apie 15-20 min. 180 laipsnių orkaitėje.&lt;/p&gt;', '3364.jpg'),
(280, 'Keksiukai su persikais', '&lt;p&gt;Kambario temperatūros sviestą išsukti su cukrumi. Įmaišyti išplaktą kiaušinį.&lt;/p&gt;&lt;p&gt;Į tešlą įmaišyti miltus, vanilinį cukrų, žiupsnelį druskos, cinamoną ir sodą.&lt;/p&gt;&lt;p&gt;Galima viską tiesiog išplakti su mikseriu.&lt;/p&gt;&lt;p&gt;Tešla turi būti vientisa t.y. be gumuliukų.&lt;/p&gt;&lt;p&gt;Tešlą supilstyti į keksiukų formeles.&lt;/p&gt;&lt;p&gt;Persikus gerai nuplauti ir supjaustyti skiltelėmis.&lt;/p&gt;&lt;p&gt;Ant kiekvieno keksiuko viršaus įspausti po 2-3 persiko skilteles.&lt;/p&gt;&lt;p&gt;Kepti 170-180 laipsnių orkaitėje 20-25 minutes.&lt;/p&gt;&lt;p&gt;Skanaus&lt;/p&gt;', '2221.jpg'),
(281, 'Naminis likeris', '&lt;p&gt;Degtinę galima praskiesti virintu vandeniu. Jei degtinės 300 ml, tai vandens iki 100 ml.&lt;/p&gt;&lt;p&gt;Į plakimo indą supilame degtinę, kondensuotą pieną, suberiame kavos miltelius ir vanilinį cukrų. Viską gerai išplakame.&lt;/p&gt;', '1855.jpg'),
(282, 'Keptas upėtakis', '&lt;p&gt;Išvalytą upėtakį pašlakstyti citrinos sultimis, pabarstyti pipirais ir druska pagal skonį. Palikti apie 10 min. marinuotis. Kepti apie 8 min., iš abiejų pusių, grilio keptuvėje, įpylus 2 šaukštus &quot;Obelių&quot; aliejau.\n                                                                Bulves supjaustyti labai plonais griežinėliais ir iškepti nedideliame kiekyje aliejaus.\n                                                                Salotoms supjaustyti ridikėlius, apelsiną, salotos lapus ir pašlakstyti citrinos sultimis bei &quot;Obelių&quot; aliejumi.&lt;/p&gt;', '6615.jpg'),
(283, 'Arbūzinis kokteilis', '&lt;p&gt;Arbūzo minkštimą supjaustome ir kartu su mėtų lapais sudedame į plakimo dubenį. Plakame, kol gaunasi vientisa arbūzų masė.&lt;/p&gt;&lt;p&gt;Supilame sirupą ir dar kartą viską suplakame.&lt;/p&gt;&lt;p&gt;Nerealiai skanu.&lt;/p&gt;&lt;p&gt;Kas mėgsta saldžiai gali dėti medaus. ;)&lt;/p&gt;', '3162.jpg'),
(284, 'Kokteilio Gin Martini gaminimas', '&lt;p&gt;Šiame vaizdelyje pamatysite kaip pasigaminti Gin Martini kokteilį. Pateikiama anglų kalba.&lt;/p&gt;', '6174.jpg'),
(285, 'Pyragas „Sniegas“', '&lt;p&gt;Iš kambario temperatūros sviesto, cukraus, miltų, likerio ir kokoso drožlių (50 g) išminkykite tešlą. Susukite tešlą į maistinę plėvelę ir palaikykite šaldytuve 30 minučių.&lt;/p&gt;&lt;p&gt;Po to tešlą gerai iškočiokite ir išpjaukite iš tešlos lakšto 22 cm skersmens apvalų lakštą. Kepkite jį uždėję ant kepimo popieriaus 15 minučių 180-200° C laipsnių orkaitėje.&lt;/p&gt;&lt;p&gt;22 cm skersmens inde išplakiau kokoso pieną, varškę, sutirštintą pieną ir ištirpintą želatiną. Į šią masę įmaišiau migdolus. Su visu dubenėliu palikau šaldytuve, kad sustingtų.&lt;/p&gt;&lt;p&gt;Po to dubenėlį apverčiau ant iškepusio lakšto ir iškrėčiau sustingusią želatiną. Apibarsčiau gausiai kokoso drožlėmis. :)&lt;/p&gt;', '2556.jpg'),
(286, 'Šviežių daržovių salotos su tunu ir fetos sūriu', '&lt;p&gt;Į lėkštę sudedame špinatus, rukolą, svogūno griežinėlius, kubeliais supjaustytą agurką, pomidorų skilteles, tuną ir fetos sūrį. Į aliejų išspaudžiame pusę citrinos, įberiame druskos ir pipirų. Viską užpilame ant paruoštų salotų. Pabarstome baziliko ir kalendros prieskoniais, įdedame krapų.&lt;/p&gt;&lt;p&gt;Skanaus!&lt;/p&gt;', '3596.jpg'),
(287, 'Sveikas lečo be acto', '&lt;p&gt;Pomidorus paverdame  kokia 15 min., po to sudedame papriką ir verdame iki 40 min.. Įdedame smulkintą česnaką, smulkinto čili pipiro, cukrų ir supilame aliejų,  paverdame dar apie15 min. Sudedame į gerai sterilizuotus stiklainius ir uždarome.Tai nuostabiai skanus ir sveikas maistas.&lt;/p&gt;', '5181.jpg'),
(288, 'Daržovių pieniška sriuba', '&lt;p&gt;Į pasūdytą verdantį vandenį sudedame kubeliais supjaustytą morką, žirnelius ir susmulkintą kopūstą.&lt;/p&gt;&lt;p&gt;Po 5 min. sudedame kubeliais supjaustytas bulves.&lt;/p&gt;&lt;p&gt;Verdame, kol daržovės suminkštėja.&lt;/p&gt;&lt;p&gt;Tada supilame pieną. Kai pieno puta pradeda kilti, puodą nukeliame nuo ugnies ir įdedame sviesto.&lt;/p&gt;', '4236.jpg'),
(289, 'Mėlynių pyragas', '&lt;p&gt;Miltus sumaišyti kartu su kepimo milteliais. Sviestą išsukti su cukrumi iki purios masės, vis maišant įmušti po vieną kiaušinį. Supilti jogurtą ir vanilės ekstraktą, vėl gerai išmaišyti. Suberti miltus ir atsargiai išmaišyti.\n                                                                Pusę tešlos dėti į kepimo formą, suberti pusę mėlynių, tada vėl tešlą, mėlynes ir viršų pabarstyti ruduoju cukrumi.\n                                                                Kepti 180°C įkaitintoje orkaitėje valandą laiko.&lt;/p&gt;', '3849.jpg'),
(290, 'Spanguolių likeris', '&lt;p&gt;Spanguoles sumalti, užpilti degtine, supilti į sandariai uždaromą indą ir 3-4 dienas palaikyti.&lt;/p&gt;&lt;p&gt;Tuomet nukošti per keturgubos marlės filtrą, supilti cukrų ir pakaitinti iki virimo, bet neužvirti.&lt;/p&gt;&lt;p&gt;Nukėlus nuo ugnies penkioms minutėms įmesti marlėje surištus prieskonius.&lt;/p&gt;&lt;p&gt;Prieskonius išėmus supilstyti į butelius ir laikyti vėsioje vietoje.&lt;/p&gt;', '1668.jpg'),
(291, 'Prancūziškas "Cremee Brulee"', '&lt;p&gt;1. Užkaitinkite pieno, grietinės ir perpus padalintos vanilės mišinį. Atvėsinkite ir po to laikykite ties 70°C temperatūra.&lt;/p&gt;&lt;p&gt;2. Cukrų su tryniais suplakite, kol jie taps balti.&lt;/p&gt;&lt;p&gt;3. Sumaišykite pieną su tryniais.&lt;/p&gt;&lt;p&gt;4. Masę supilstykite į mažus indelius, tinkamus kepimui orkaitėje.&lt;/p&gt;&lt;p&gt;5. Ištieskite kepimo popieriumi skardą ir įpilkite vandens. Kepkite 160-170°C temperatūroje apie 20 min. (kol įgaus norimą tekstūrą).&lt;/p&gt;&lt;p&gt;6. Išimkite iš orkaitės. Laikykite šaldytuve nuo 1 iki 3 val.&lt;/p&gt;&lt;p&gt;7. Išėmę iš šaldytuvo, apibarstykite cukrumi ir jį uždeginkite.&lt;/p&gt;&lt;p&gt;Išmokite gaminti šį ir kitus Prancūziškus patiekalus Čiop Čiop kulinarijos kursuose! Artimiausias Prancūzijos virtuvės kursas birželio 18 d.:http://www.ciopciop.lt/kursas/prancuzijos-virtuve-28&lt;/p&gt;', '3317.jpg'),
(292, 'Kiaušiniai garuose', '&lt;p&gt;Į silikono formeles dėti po puselę pomidoro, tada įmušti kiaušinį, barstyti druskos bei pipirų. Į garų puodą supilti dvi stiklines vandens, o ant sekančio sluoksnio padėti formeles su kiaušiniais. Virti 15 - 20 minučių.&lt;/p&gt;', '6271.jpg'),
(293, 'Prancūziškas pyragas', '&lt;p&gt;Sluoksniuotos tešlos lakštus iškočiokite. Vieną tešlos lakštą įtieskite į kepimo formą.&lt;/p&gt;&lt;p&gt;Bulves supjaustykite kubeliais. Česnaką sugrūskite, žalumynus sukapokite. Bulves sumaišykite su grietine, česnaku, žalumynais, druska ir pipirais.&lt;/p&gt;&lt;p&gt;Bulvių įdaro pridėkite amt tešlos. Antrąjį tešlos lakštą uždekite ant įdaro ir įpjaukite. Viršų aptepkite kiaušinio plakiniu.&lt;/p&gt;&lt;p&gt;Pašaukite į 170 laipsnių pagal Celsijų orkaitę 20-30 minučių.&lt;/p&gt;', '3108.jpg'),
(294, 'Troškintas triušis', '&lt;p&gt;Petražoles ir krapus susmulkinkite ir sumaišykite su pipirais bei druska. Marinatu gerai įtrinkite triušį ir palikite per nakt marinuotis.\n                                                                Stambiai supjaustykite triušį ir iš visų pusių apkepkite karštame aliejuje, ant didelės ugnies.\n                                                                Į puodą su verdančiu vandeniu sudėkite išmarinuotą triušieną, įmeskite sultinio kubelį  ir troškinkite apie valandą laiko.&lt;/p&gt;&lt;p&gt;Receptą radau dienraštyje &quot;15 minučių&quot;. Patiko marinato idėja, tačiau, kadangi šeima labai mėgsta ir krapus, nutariau papildyti šį receptą.&lt;/p&gt;&lt;p&gt;Triušiena, triušienos patiekalai jau nuo seno siejosi su Prancūzijos virtuve. Nemažai patiekalų iš prancūzų virtuvės atkeliavo ir pas mus, dar viduramžiais. Galima sakyti, kad triušienos valgymo madą mes, lietuviai, pasiskolinome iš prancūzų.&lt;/p&gt;', '6608.jpg'),
(295, 'Žaismingi seniai besmegeniai', '&lt;p&gt;Ant medinio iešmelio verti vieną pailgą zefyrą ir dar vieną, perpjautą pusiau. Tada užmauti po guminuką, kuris bus besmegenio kepurė. Iš pabarstukų padaryti akis, burną bei sagas.&lt;/p&gt;', '5619.jpg'),
(296, 'Liežuvis su grybais', '&lt;p&gt;Svogūnus pakepinti aliejuje kartu su grybais, įpilti grietinėlės, sudėti sutarkuotą morką, pagardinti druska bei pipirais. Į kepimo skardą dėti virtą liežuvį, užpilti padažą ir troškinti orkaitėje apie 15 minučių.&lt;/p&gt;&lt;p&gt;Bajorų virtuvėje buvo populiaru naudot mėsos subproduktus, derinti mėsą su grybais. Sotūs patiekalai buvo tarsi bajoriškosios virtuvės vizitinė kortelė.&lt;/p&gt;&lt;p&gt;Šaltinis: Savaitraštis &quot;TV savaitė&quot;&lt;/p&gt;', '6585.jpg');
